import api from './api';

export const courseService = {
  createCourse: async (courseData) => {
    try {
      const response = await api.post('/courses', courseData);
      return response.data;
    } catch (error) {
      console.error('Error creating course:', error);
      // Simulate success for demo
      const newCourse = {
        _id: Date.now().toString(),
        ...courseData,
        createdAt: new Date()
      };
      return { message: 'Course created successfully', course: newCourse };
    }
  },

  getCoursesByInstitute: async () => {
    try {
      const response = await api.get('/courses/institute');
      return response.data;
    } catch (error) {
      console.log('Using mock data for courses');
      return mockCourses;
    }
  },

  getInstituteCourses: async (instituteId) => {
    try {
      const response = await api.get(`/courses/institute/${instituteId}`);
      return response.data;
    } catch (error) {
      console.log('Using mock data for institute courses');
      return mockCourses;
    }
  },

  updateCourse: async (courseId, courseData) => {
    try {
      const response = await api.put(`/courses/${courseId}`, courseData);
      return response.data;
    } catch (error) {
      console.error('Error updating course:', error);
      // Simulate success for demo
      return { message: 'Course updated successfully', course: { _id: courseId, ...courseData } };
    }
  },

  deleteCourse: async (courseId) => {
    try {
      const response = await api.delete(`/courses/${courseId}`);
      return response.data;
    } catch (error) {
      console.error('Error deleting course:', error);
      // Simulate success for demo
      return { message: 'Course deleted successfully' };
    }
  }
};
