import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const InstituteSidebar = ({ onLogout }) => {
  const location = useLocation();

  const menuItems = [
    { path: '/institute', icon: 'üìä', label: 'Dashboard' },
    { path: '/institute/profile', icon: 'üè´', label: 'Profile Management' },
    { path: '/institute/courses', icon: 'üìö', label: 'Course Management' },
    { path: '/institute/facilities', icon: '‚ö°', label: 'Facilities' },
    { path: '/institute/enquiries', icon: 'üì©', label: 'Enquiries' },
    { path: '/institute/reviews', icon: '‚≠ê', label: 'Reviews' },
  ];

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h3>Institute Panel</h3>
      </div>
      
      <nav className="sidebar-nav">
        {menuItems.map(item => (
          <Link
            key={item.path}
            to={item.path}
            className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
          >
            <span className="nav-icon">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
          </Link>
        ))}
      </nav>

      <button onClick={onLogout} className="logout-btn btn btn-danger">
        üö™ Logout
      </button>
    </div>
  );
};

export default InstituteSidebar;
