import React, { useState, useEffect } from 'react';
import { reviewAPI } from '../../services/api';
import './InstituteDashboard.css';

const Reviews = () => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchReviews();
  }, []);

  const fetchReviews = async () => {
    try {
      // In real implementation, you'd have an API endpoint for institute reviews
      const mockReviews = [
        {
          _id: '1',
          userName: 'Amit Kumar',
          rating: 5,
          reviewText: 'Excellent infrastructure and faculty. My child has shown remarkable improvement since joining.',
          approvalStatus: 'approved',
          createdAt: new Date('2024-01-10').toISOString()
        },
        {
          _id: '2',
          userName: 'Neha Singh',
          rating: 4,
          reviewText: 'Good facilities but sports infrastructure could be improved. Overall satisfied with the education quality.',
          approvalStatus: 'approved',
          createdAt: new Date('2024-01-08').toISOString()
        },
        {
          _id: '3',
          userName: 'Rajesh Mehta',
          rating: 3,
          reviewText: 'Average experience. Communication with administration needs improvement.',
          approvalStatus: 'pending',
          createdAt: new Date('2024-01-12').toISOString()
        }
      ];
      setReviews(mockReviews);
    } catch (error) {
      console.error('Error fetching reviews:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFlagReview = async (reviewId) => {
    try {
      // In real implementation, you'd call an API to flag the review
      console.log('Flagged review:', reviewId);
      alert('Review has been flagged for admin review.');
    } catch (error) {
      console.error('Error flagging review:', error);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: '2-digit',
      month: 'short',
      year: 'numeric'
    });
  };

  const renderStars = (rating) => {
    return (
      <div className="star-rating">
        {[1, 2, 3, 4, 5].map(star => (
          <span
            key={star}
            className={star <= rating ? 'star filled' : 'star'}
          >
            ★
          </span>
        ))}
      </div>
    );
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'status-pending', text: 'Pending' },
      approved: { class: 'status-approved', text: 'Approved' },
      rejected: { class: 'status-rejected', text: 'Rejected' }
    };
    
    const config = statusConfig[status] || statusConfig.pending;
    return <span className={`status-badge ${config.class}`}>{config.text}</span>;
  };

  if (loading) {
    return <div className="loading">Loading reviews...</div>;
  }

  const approvedReviews = reviews.filter(review => review.approvalStatus === 'approved');
  const pendingReviews = reviews.filter(review => review.approvalStatus === 'pending');

  return (
    <div className="reviews-management">
      <div className="section-header">
        <h2>Student Reviews</h2>
        <div className="review-stats">
          <div className="stat-card">
            <span className="stat-number">{reviews.length}</span>
            <span className="stat-label">Total Reviews</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{approvedReviews.length}</span>
            <span className="stat-label">Approved</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">{pendingReviews.length}</span>
            <span className="stat-label">Pending</span>
          </div>
          <div className="stat-card">
            <span className="stat-number">
              {reviews.length > 0 
                ? (reviews.reduce((sum, review) => sum + review.rating, 0) / reviews.length).toFixed(1)
                : '0.0'
              }
            </span>
            <span className="stat-label">Average Rating</span>
          </div>
        </div>
      </div>

      <div className="reviews-content">
        {/* Pending Reviews Section */}
        {pendingReviews.length > 0 && (
          <div className="reviews-section">
            <h3>Pending Approval ({pendingReviews.length})</h3>
            <div className="reviews-grid">
              {pendingReviews.map(review => (
                <div key={review._id} className="review-card pending">
                  <div className="review-header">
                    <div className="reviewer-info">
                      <h4>{review.userName}</h4>
                      {renderStars(review.rating)}
                    </div>
                    <div className="review-meta">
                      {getStatusBadge(review.approvalStatus)}
                      <span className="review-date">{formatDate(review.createdAt)}</span>
                    </div>
                  </div>
                  <p className="review-text">{review.reviewText}</p>
                  <div className="review-actions">
                    <button 
                      className="btn-flag"
                      onClick={() => handleFlagReview(review._id)}
                    >
                      ⚐ Flag as Inappropriate
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Approved Reviews Section */}
        <div className="reviews-section">
          <h3>Approved Reviews ({approvedReviews.length})</h3>
          {approvedReviews.length === 0 ? (
            <div className="empty-state">
              <p>No approved reviews yet.</p>
            </div>
          ) : (
            <div className="reviews-grid">
              {approvedReviews.map(review => (
                <div key={review._id} className="review-card approved">
                  <div className="review-header">
                    <div className="reviewer-info">
                      <h4>{review.userName}</h4>
                      {renderStars(review.rating)}
                    </div>
                    <div className="review-meta">
                      {getStatusBadge(review.approvalStatus)}
                      <span className="review-date">{formatDate(review.createdAt)}</span>
                    </div>
                  </div>
                  <p className="review-text">{review.reviewText}</p>
                  <div className="review-actions">
                    <button 
                      className="btn-flag"
                      onClick={() => handleFlagReview(review._id)}
                    >
                      ⚐ Flag as Inappropriate
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* No Reviews State */}
        {reviews.length === 0 && (
          <div className="empty-state">
            <div className="empty-icon">⭐</div>
            <h3>No Reviews Yet</h3>
            <p>Student reviews will appear here once they start reviewing your institute.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Reviews;
