import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import InstituteSidebar from './InstituteSidebar';
import ProfileManagement from './ProfileManagement';
import CourseManagement from './CourseManagement';
import FacilitiesManagement from './FacilitiesManagement';
import Enquiries from './Enquiries';
import Reviews from './Reviews';
import { instituteAPI, enquiryAPI, reviewAPI } from '../../services/api';
import './InstituteDashboard.css';

const InstituteDashboard = () => {
  const [institute, setInstitute] = useState(null);
  const [stats, setStats] = useState({
    totalEnquiries: 0,
    pendingEnquiries: 0,
    totalReviews: 0,
    averageRating: 0
  });
  const [loading, setLoading] = useState(true);
  
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    fetchInstituteData();
    fetchDashboardStats();
  }, []);

  const fetchInstituteData = async () => {
    try {
      const response = await instituteAPI.getById(user.institute?._id);
      setInstitute(response.data.institute);
    } catch (error) {
      console.error('Error fetching institute data:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchDashboardStats = async () => {
    try {
      // Get real stats from APIs
      const [enquiriesRes, reviewsRes] = await Promise.all([
        enquiryAPI.getByInstitute(),
        reviewAPI.getByInstitute(user.institute?._id)
      ]);

      const enquiries = enquiriesRes.data;
      const reviews = reviewsRes.data;

      setStats({
        totalEnquiries: enquiries.length,
        pendingEnquiries: enquiries.filter(e => e.status === 'pending').length,
        totalReviews: reviews.length,
        averageRating: reviews.reduce((acc, review) => acc + review.rating, 0) / reviews.length || 0
      });
    } catch (error) {
      console.error('Error fetching stats:', error);
      // Start with zero stats for new institutes
      setStats({
        totalEnquiries: 0,
        pendingEnquiries: 0,
        totalReviews: 0,
        averageRating: 0
      });
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading your dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="institute-dashboard">
      <InstituteSidebar onLogout={handleLogout} />
      
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Institute Dashboard</h1>
          <div className="welcome-message">
            Welcome back, {institute?.name || user?.name}
            {!institute && <span className="setup-reminder"> - Complete your profile setup</span>}
          </div>
        </div>

        {location.pathname === '/institute-dashboard' && (
          <div className="dashboard-overview">
            <div className="stats-grid">
              <div className="stat-card">
                <div className="stat-icon">üìä</div>
                <div className="stat-info">
                  <h3>{stats.totalEnquiries}</h3>
                  <p>Total Enquiries</p>
                </div>
              </div>
              
              <div className="stat-card">
                <div className="stat-icon">‚è≥</div>
                <div className="stat-info">
                  <h3>{stats.pendingEnquiries}</h3>
                  <p>Pending Enquiries</p>
                </div>
              </div>
              
              <div className="stat-card">
                <div className="stat-icon">‚≠ê</div>
                <div className="stat-info">
                  <h3>{stats.totalReviews}</h3>
                  <p>Total Reviews</p>
                </div>
              </div>
              
              <div className="stat-card">
                <div className="stat-icon">üìà</div>
                <div className="stat-info">
                  <h3>{stats.averageRating.toFixed(1)}</h3>
                  <p>Average Rating</p>
                </div>
              </div>
            </div>

            <div className="setup-guide">
              <h3>Get Started</h3>
              <div className="setup-steps">
                <div className={`setup-step ${institute ? 'completed' : ''}`}>
                  <span className="step-number">1</span>
                  <span className="step-text">Complete Institute Profile</span>
                </div>
                <div className="setup-step">
                  <span className="step-number">2</span>
                  <span className="step-text">Add Courses & Programs</span>
                </div>
                <div className="setup-step">
                  <span className="step-number">3</span>
                  <span className="step-text">List Facilities & Amenities</span>
                </div>
                <div className="setup-step">
                  <span className="step-number">4</span>
                  <span className="step-text">Start Receiving Enquiries</span>
                </div>
              </div>
            </div>
          </div>
        )}

        <Routes>
          <Route path="profile" element={<ProfileManagement institute={institute} onUpdate={fetchInstituteData} />} />
          <Route path="courses" element={<CourseManagement institute={institute} onUpdate={fetchInstituteData} />} />
          <Route path="facilities" element={<FacilitiesManagement institute={institute} onUpdate={fetchInstituteData} />} />
          <Route path="enquiries" element={<Enquiries />} />
          <Route path="reviews" element={<Reviews />} />
        </Routes>
      </div>
    </div>
  );
};

export default InstituteDashboard;
