import React, { useState, useEffect } from 'react';
import { adminService } from '../../services/api';
import './AdminPanel.css';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await adminService.getUsers();
      setUsers(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching users:', error);
      setLoading(false);
    }
  };

  const handleUserStatus = async (userId, isActive) => {
    try {
      await adminService.updateUserStatus(userId, isActive);
      setMessage(`User ${isActive ? 'activated' : 'deactivated'} successfully!`);
      fetchUsers();
    } catch (error) {
      setMessage('Error updating user status.');
    }
  };

  const getRoleBadge = (role) => {
    const roleConfig = {
      user: { class: 'user', label: 'Student/Parent' },
      institute: { class: 'institute', label: 'Institute' },
      admin: { class: 'admin', label: 'Admin' }
    };
    
    const config = roleConfig[role] || roleConfig.user;
    return <span className={`role-badge ${config.class}`}>{config.label}</span>;
  };

  if (loading) {
    return <div className="loading">Loading users...</div>;
  }

  return (
    <div className="manage-users">
      <div className="admin-section">
        <h2>Manage Users</h2>
        
        {message && (
          <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
            {message}
          </div>
        )}

        <div className="users-stats">
          <div className="stat-card">
            <h3>Total Users</h3>
            <p>{users.length}</p>
          </div>
          <div className="stat-card">
            <h3>Students/Parents</h3>
            <p>{users.filter(u => u.role === 'user').length}</p>
          </div>
          <div className="stat-card">
            <h3>Institutes</h3>
            <p>{users.filter(u => u.role === 'institute').length}</p>
          </div>
          <div className="stat-card">
            <h3>Active Users</h3>
            <p>{users.filter(u => u.isActive).length}</p>
          </div>
        </div>

        <div className="users-list">
          {users.map(user => (
            <div key={user._id} className="user-card">
              <div className="user-header">
                <div className="user-basic">
                  <div className="user-avatar">
                    {user.name?.charAt(0).toUpperCase()}
                  </div>
                  <div>
                    <h3>{user.name}</h3>
                    <p className="user-email">{user.email}</p>
                    <p className="user-phone">{user.phone}</p>
                  </div>
                </div>
                
                <div className="user-meta">
                  {getRoleBadge(user.role)}
                  <span className={`status ${user.isActive ? 'active' : 'inactive'}`}>
                    {user.isActive ? 'Active' : 'Inactive'}
                  </span>
                </div>
              </div>

              <div className="user-details">
                <div className="detail-item">
                  <strong>Registered:</strong>
                  <span>{new Date(user.createdAt).toLocaleDateString()}</span>
                </div>
                <div className="detail-item">
                  <strong>Last Updated:</strong>
                  <span>{new Date(user.updatedAt).toLocaleDateString()}</span>
                </div>
              </div>

              <div className="user-actions">
                <button
                  onClick={() => handleUserStatus(user._id, !user.isActive)}
                  className={user.isActive ? 'btn-deactivate' : 'btn-activate'}
                >
                  {user.isActive ? 'Deactivate User' : 'Activate User'}
                </button>
              </div>
            </div>
          ))}
        </div>

        {users.length === 0 && (
          <div className="no-data">
            <p>No users found.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ManageUsers;
