import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { instituteService } from '../../services/api';
import './UserPanel.css';

const InstituteList = () => {
  const [institutes, setInstitutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    search: '',
    category: '',
    city: '',
    minFees: '',
    maxFees: '',
    facilities: '',
    minRating: ''
  });
  const [pagination, setPagination] = useState({
    page: 1,
    totalPages: 1,
    total: 0
  });

  useEffect(() => {
    fetchInstitutes();
  }, [filters, pagination.page]);

  const fetchInstitutes = async () => {
    try {
      const response = await instituteService.getAll({
        ...filters,
        page: pagination.page,
        limit: 12
      });
      setInstitutes(response.data.institutes);
      setPagination(prev => ({
        ...prev,
        totalPages: response.data.totalPages,
        total: response.data.total
      }));
      setLoading(false);
    } catch (error) {
      console.error('Error fetching institutes:', error);
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
    setPagination(prev => ({ ...prev, page: 1 }));
  };

  const clearFilters = () => {
    setFilters({
      search: '',
      category: '',
      city: '',
      minFees: '',
      maxFees: '',
      facilities: '',
      minRating: ''
    });
    setPagination(prev => ({ ...prev, page: 1 }));
  };

  if (loading) {
    return <div className="loading">Loading institutes...</div>;
  }

  return (
    <div className="institute-list-page">
      <div className="container">
        <div className="page-header">
          <h1>Find Educational Institutes</h1>
          <p>Discover the best schools, colleges, and coaching centers</p>
        </div>

        {/* Filters */}
        <div className="filters-section">
          <div className="filters-grid">
            <div className="filter-group">
              <label>Search</label>
              <input
                type="text"
                name="search"
                value={filters.search}
                onChange={handleFilterChange}
                placeholder="Search by name or description..."
              />
            </div>

            <div className="filter-group">
              <label>Category</label>
              <select
                name="category"
                value={filters.category}
                onChange={handleFilterChange}
              >
                <option value="">All Categories</option>
                <option value="school">School</option>
                <option value="college">College</option>
                <option value="coaching">Coaching Center</option>
                <option value="preschool">Preschool</option>
                <option value="university">University</option>
              </select>
            </div>

            <div className="filter-group">
              <label>City</label>
              <input
                type="text"
                name="city"
                value={filters.city}
                onChange={handleFilterChange}
                placeholder="Enter city..."
              />
            </div>

            <div className="filter-group">
              <label>Min Rating</label>
              <select
                name="minRating"
                value={filters.minRating}
                onChange={handleFilterChange}
              >
                <option value="">Any Rating</option>
                <option value="4">4+ Stars</option>
                <option value="3">3+ Stars</option>
                <option value="2">2+ Stars</option>
                <option value="1">1+ Stars</option>
              </select>
            </div>

            <div className="filter-group">
              <label>Min Fees (‚Çπ)</label>
              <input
                type="number"
                name="minFees"
                value={filters.minFees}
                onChange={handleFilterChange}
                placeholder="Min"
              />
            </div>

            <div className="filter-group">
              <label>Max Fees (‚Çπ)</label>
              <input
                type="number"
                name="maxFees"
                value={filters.maxFees}
                onChange={handleFilterChange}
                placeholder="Max"
              />
            </div>

            <div className="filter-group">
              <label>Facilities</label>
              <select
                name="facilities"
                value={filters.facilities}
                onChange={handleFilterChange}
              >
                <option value="">Any Facility</option>
                <option value="library">Library</option>
                <option value="sports">Sports</option>
                <option value="lab">Laboratory</option>
                <option value="hostel">Hostel</option>
                <option value="transport">Transport</option>
              </select>
            </div>

            <div className="filter-actions">
              <button onClick={clearFilters} className="btn-secondary">
                Clear Filters
              </button>
            </div>
          </div>
        </div>

        {/* Results */}
        <div className="results-section">
          <div className="results-header">
            <h2>{pagination.total} Institutes Found</h2>
          </div>

          {institutes.length === 0 ? (
            <div className="no-results">
              <h3>No institutes found</h3>
              <p>Try adjusting your search filters</p>
            </div>
          ) : (
            <>
              <div className="institutes-grid">
                {institutes.map(institute => (
                  <div key={institute._id} className="institute-card">
                    <Link to={`/institutes/${institute._id}`} className="card-link">
                      <div className="card-image">
                        {institute.logo ? (
                          <img 
                            src={`http://localhost:5000/${institute.logo}`} 
                            alt={institute.name}
                          />
                        ) : (
                          <div className="no-image">üè´</div>
                        )}
                      </div>
                      
                      <div className="card-content">
                        <h3>{institute.name}</h3>
                        <p className="category">{institute.category} ‚Ä¢ {institute.affiliation}</p>
                        <p className="location">üìç {institute.address?.city}, {institute.address?.state}</p>
                        
                        <div className="rating">
                          <span className="stars">
                            {'‚≠ê'.repeat(Math.floor(institute.rating?.average || 0))}
                            {institute.rating?.average || 0}/5
                          </span>
                          <span className="review-count">
                            ({institute.rating?.count || 0} reviews)
                          </span>
                        </div>

                        <div className="facilities">
                          {institute.facilities?.slice(0, 3).map((facility, index) => (
                            <span key={index} className="facility-tag">{facility}</span>
                          ))}
                          {institute.facilities?.length > 3 && (
                            <span className="facility-tag">+{institute.facilities.length - 3} more</span>
                          )}
                        </div>

                        <div className="courses-count">
                          üìö {institute.courses?.length || 0} Courses
                        </div>
                      </div>
                    </Link>
                  </div>
                ))}
              </div>

              {/* Pagination */}
              {pagination.totalPages > 1 && (
                <div className="pagination">
                  <button
                    onClick={() => setPagination(prev => ({ ...prev, page: prev.page - 1 }))}
                    disabled={pagination.page === 1}
                    className="pagination-btn"
                  >
                    Previous
                  </button>
                  
                  <span className="pagination-info">
                    Page {pagination.page} of {pagination.totalPages}
                  </span>
                  
                  <button
                    onClick={() => setPagination(prev => ({ ...prev, page: prev.page + 1 }))}
                    disabled={pagination.page === pagination.totalPages}
                    className="pagination-btn"
                  >
                    Next
                  </button>
                </div>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default InstituteList;
