import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import UserSidebar from './UserSidebar';
import InstituteList from './InstituteList';
import ReviewForm from './ReviewForm';
import EnquiryForm from './EnquiryForm';
import { useAuth } from '../../context/AuthContext';
import { reviewAPI, enquiryAPI } from '../../services/api';
import './UserPanel.css';

const UserDashboard = () => {
  const { user } = useAuth();
  const [userStats, setUserStats] = useState({
    reviewsGiven: 0,
    enquiriesSent: 0,
    institutesViewed: 0
  });

  useEffect(() => {
    fetchUserStats();
  }, []);

  const fetchUserStats = async () => {
    try {
      const [reviewsRes, enquiriesRes] = await Promise.all([
        reviewAPI.getAll(),
        enquiryAPI.getAll()
      ]);

      // Filter user's own data
      const userReviews = reviewsRes.data.filter(review => review.user?._id === user.id);
      const userEnquiries = enquiriesRes.data.filter(enquiry => enquiry.user === user.id);

      setUserStats({
        reviewsGiven: userReviews.length,
        enquiriesSent: userEnquiries.length,
        institutesViewed: new Set([
          ...userReviews.map(r => r.institute?._id),
          ...userEnquiries.map(e => e.institute?._id)
        ].filter(Boolean)).size
      });
    } catch (error) {
      console.error('Error fetching user stats:', error);
    }
  };

  return (
    <div className="user-dashboard">
      <UserSidebar />
      <div className="dashboard-content">
        <Routes>
          <Route path="/" element={<DashboardHome userStats={userStats} user={user} />} />
          <Route path="/institutes" element={<InstituteList />} />
          <Route path="/reviews" element={<ReviewForm />} />
          <Route path="/enquiries" element={<EnquiryForm />} />
          <Route path="*" element={<Navigate to="/user/dashboard" replace />} />
        </Routes>
      </div>
    </div>
  );
};

const DashboardHome = ({ userStats, user }) => {
  return (
    <div className="dashboard-home">
      <div className="dashboard-header">
        <h1>Welcome back, {user.name}!</h1>
        <p>Find the perfect educational institution for your needs</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üè´</div>
          <div className="stat-info">
            <h3>{userStats.institutesViewed}</h3>
            <p>Institutes Viewed</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">‚≠ê</div>
          <div className="stat-info">
            <h3>{userStats.reviewsGiven}</h3>
            <p>Reviews Given</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üìß</div>
          <div className="stat-info">
            <h3>{userStats.enquiriesSent}</h3>
            <p>Enquiries Sent</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üë§</div>
          <div className="stat-info">
            <h3>Active</h3>
            <p>Account Status</p>
          </div>
        </div>
      </div>

      <div className="quick-actions">
        <h2>Quick Actions</h2>
        <div className="actions-grid">
          <div className="action-card" onClick={() => window.location.href = '/user/dashboard/institutes'}>
            <span className="action-icon">üîç</span>
            <span className="action-text">Browse Institutes</span>
            <p>Discover educational institutions</p>
          </div>

          <div className="action-card" onClick={() => window.location.href = '/user/dashboard/reviews'}>
            <span className="action-icon">‚≠ê</span>
            <span className="action-text">Write Reviews</span>
            <p>Share your experiences</p>
          </div>

          <div className="action-card" onClick={() => window.location.href = '/user/dashboard/enquiries'}>
            <span className="action-icon">üìß</span>
            <span className="action-text">Send Enquiries</span>
            <p>Contact institutes directly</p>
          </div>

          <div className="action-card" onClick={() => window.location.href = '/institutes'}>
            <span className="action-icon">üìä</span>
            <span className="action-text">Compare Institutes</span>
