import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useApp } from '../../context/AppContext';
import { facilityAPI } from '../../services/api';
import InstituteSidebar from './InstituteSidebar';
import './InstituteDashboard.css';

const FacilitiesManagement = () => {
  const { user } = useAuth();
  const { addAlert } = useApp();
  const [facilities, setFacilities] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingFacility, setEditingFacility] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    type: '',
    capacity: '',
    availability: 'available'
  });

  useEffect(() => {
    fetchFacilities();
  }, [user]);

  const fetchFacilities = async () => {
    try {
      const response = await facilityAPI.getByInstitute(user.instituteId);
      setFacilities(response.data);
    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to load facilities'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const facilityData = {
        ...formData,
        institute: user.instituteId
      };

      if (editingFacility) {
        await facilityAPI.update(editingFacility._id, facilityData);
        addAlert({
          type: 'success',
          message: 'Facility updated successfully'
        });
      } else {
        await facilityAPI.create(facilityData);
        addAlert({
          type: 'success',
          message: 'Facility created successfully'
        });
      }

      resetForm();
      fetchFacilities();
    } catch (error) {
      addAlert({
        type: 'error',
        message: `Failed to ${editingFacility ? 'update' : 'create'} facility`
      });
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      type: '',
      capacity: '',
      availability: 'available'
    });
    setEditingFacility(null);
    setShowForm(false);
  };

  const handleEdit = (facility) => {
    setFormData({
      name: facility.name,
      description: facility.description,
      type: facility.type,
      capacity: facility.capacity,
      availability: facility.availability
    });
    setEditingFacility(facility);
    setShowForm(true);
  };

  const handleDelete = async (facilityId) => {
    if (window.confirm('Are you sure you want to delete this facility?')) {
      try {
        await facilityAPI.delete(facilityId);
        addAlert({
          type: 'success',
          message: 'Facility deleted successfully'
        });
        fetchFacilities();
      } catch (error) {
        addAlert({
          type: 'error',
          message: 'Failed to delete facility'
        });
      }
    }
  };

  const facilityTypes = [
    'Library', 'Laboratory', 'Sports', 'Hostel', 'Cafeteria', 
    'Auditorium', 'Transport', 'Medical', 'Computer Lab', 'Playground'
  ];

  if (loading) {
    return (
      <div className="dashboard-container">
        <InstituteSidebar />
        <div className="dashboard-content">
          <div className="loading">Loading facilities...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <InstituteSidebar />
      <div className="dashboard-content">
        <div className="page-header">
          <h1>Facilities Management</h1>
          <button 
            onClick={() => setShowForm(true)} 
            className="btn btn-primary"
          >
            Add New Facility
          </button>
        </div>

        {showForm && (
          <div className="form-card">
            <div className="card-header">
              <h3>{editingFacility ? 'Edit Facility' : 'Add New Facility'}</h3>
              <button onClick={resetForm} className="btn btn-outline">Cancel</button>
            </div>
            <form onSubmit={handleSubmit} className="facility-form">
              <div className="form-grid">
                <div className="form-group">
                  <label className="form-label">Facility Name</label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="e.g., Main Library, Science Lab"
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Type</label>
                  <select
                    name="type"
                    value={formData.type}
                    onChange={handleInputChange}
                    className="form-control"
                    required
                  >
                    <option value="">Select Type</option>
                    {facilityTypes.map(type => (
                      <option key={type} value={type}>{type}</option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label className="form-label">Capacity</label>
                  <input
                    type="number"
                    name="capacity"
                    value={formData.capacity}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="Number of people"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Availability</label>
                  <select
                    name="availability"
                    value={formData.availability}
                    onChange={handleInputChange}
                    className="form-control"
                  >
                    <option value="available">Available</option>
                    <option value="limited">Limited</option>
                    <option value="unavailable">Unavailable</option>
                  </select>
                </div>

                <div className="form-group full-width">
                  <label className="form-label">Description</label>
                  <textarea
                    name="description"
                    value={formData.description}
                    onChange={handleInputChange}
                    className="form-control"
                    rows="3"
                    placeholder="Describe the facility, features, and amenities..."
                    required
                  />
                </div>
              </div>

              <div className="form-actions">
                <button type="submit" className="btn btn-primary">
                  {editingFacility ? 'Update Facility' : 'Create Facility'}
                </button>
              </div>
            </form>
          </div>
        )}

        <div className="facilities-grid">
          {facilities.length > 0 ? (
            facilities.map(facility => (
              <div key={facility._id} className="facility-card">
                <div className="facility-icon">
                  <i className={`fas fa-${getFacilityIcon(facility.type)}`}></i>
                </div>
                <div className="facility-content">
                  <h3>{facility.name}</h3>
                  <span className={`status-badge ${facility.availability}`}>
                    {facility.availability}
                  </span>
                  <p className="facility-type">{facility.type}</p>
                  <p className="facility-description">{facility.description}</p>
                  
                  {facility.capacity && (
                    <div className="facility-capacity">
                      <strong>Capacity:</strong> {facility.capacity} people
                    </div>
                  )}
                </div>
                <div className="facility-actions">
                  <button 
                    onClick={() => handleEdit(facility)}
                    className="btn btn-sm btn-outline"
                  >
                    Edit
                  </button>
                  <button 
                    onClick={() => handleDelete(facility._id)}
                    className="btn btn-sm btn-danger"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))
          ) : (
            <div className="no-data-card">
              <i className="fas fa-dumbbell"></i>
              <h3>No Facilities Added</h3>
              <p>Start by adding facilities to showcase your institute's infrastructure.</p>
              <button 
                onClick={() => setShowForm(true)} 
                className="btn btn-primary"
              >
                Add Your First Facility
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// Helper function to get icons for facility types
const getFacilityIcon = (type) => {
  const iconMap = {
    'Library': 'book',
    'Laboratory': 'flask',
    'Sports': 'running',
    'Hostel': 'bed',
    'Cafeteria': 'utensils',
    'Auditorium': 'theater-masks',
    'Transport': 'bus',
    'Medical': 'first-aid',
    'Computer Lab': 'desktop',
    'Playground': 'futbol'
  };
  return iconMap[type] || 'building';
};

export default FacilitiesManagement;
