import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { instituteAPI, reviewAPI, userAPI, analyticsAPI } from '../../services/api';
import './HomePage.css';

const HomePage = () => {
  const [featuredInstitutes, setFeaturedInstitutes] = useState([]);
  const [stats, setStats] = useState({
    institutes: 0,
    reviews: 0,
    students: 0,
    cities: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchHomePageData();
  }, []);

  const fetchHomePageData = async () => {
    try {
      setLoading(true);
      
      // Fetch all data in parallel
      const [institutesRes, reviewsRes, usersRes, analyticsRes] = await Promise.all([
        instituteAPI.getAll(),
        reviewAPI.getAll(),
        userAPI.getAll(),
        analyticsAPI.getDashboard().catch(() => null) // Optional analytics
      ]);

      const institutes = institutesRes.data || [];
      const reviews = reviewsRes.data || [];
      const users = usersRes.data || [];

      // Get featured institutes (approved institutes with highest ratings)
      const approvedInstitutes = institutes.filter(inst => inst.status === 'approved');
      const featured = approvedInstitutes
        .sort((a, b) => {
          // Sort by rating or by creation date if no reviews
          const aRating = calculateInstituteRating(a._id, reviews);
          const bRating = calculateInstituteRating(b._id, reviews);
          return bRating - aRating;
        })
        .slice(0, 6);

      setFeaturedInstitutes(featured);

      // Calculate dynamic stats
      const uniqueCities = new Set(approvedInstitutes.map(inst => inst.city)).size;
      const studentUsers = users.filter(user => user.role === 'user').length;

      setStats({
        institutes: approvedInstitutes.length,
        reviews: reviews.length,
        students: studentUsers,
        cities: uniqueCities
      });

    } catch (error) {
      console.error('Error fetching homepage data:', error);
      setError('Failed to load data. Please try again later.');
      
      // Set fallback data
      setStats({
        institutes: 0,
        reviews: 0,
        students: 0,
        cities: 0
      });
    } finally {
      setLoading(false);
    }
  };

  // Calculate average rating for an institute
  const calculateInstituteRating = (instituteId, reviews) => {
    const instituteReviews = reviews.filter(review => review.institute?._id === instituteId);
    if (instituteReviews.length === 0) return 0;
    
    const totalRating = instituteReviews.reduce((sum, review) => sum + review.rating, 0);
    return totalRating / instituteReviews.length;
  };

  // Get rating for display
  const getInstituteRating = (institute) => {
    // This would be better if we had ratings pre-calculated in the backend
    // For now, we'll show a placeholder
    return '4.5'; // Placeholder
  };

  if (loading) {
    return (
      <div className="homepage-loading">
        <div className="loading-spinner"></div>
        <p>Loading educational institutions...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="homepage-error">
        <h2>Oops! Something went wrong</h2>
        <p>{error}</p>
        <button onClick={fetchHomePageData} className="btn-primary">
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="homepage">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Find Your Perfect Educational Institution</h1>
          <p>Discover, compare, and choose from {stats.institutes}+ schools, colleges, and coaching centers with {stats.reviews}+ authentic reviews and ratings.</p>
          <div className="hero-buttons">
            <Link to="/institutes" className="btn-primary">Explore Institutes</Link>
            <Link to="/register" className="btn-secondary">List Your Institute</Link>
          </div>
          
          {/* Live Stats */}
          <div className="hero-stats">
            <div className="hero-stat">
              <strong>{stats.institutes}+</strong>
              <span>Institutes</span>
            </div>
            <div className="hero-stat">
              <strong>{stats.reviews}+</strong>
              <span>Reviews</span>
            </div>
            <div className="hero-stat">
              <strong>{stats.students}+</strong>
              <span>Students</span>
            </div>
          </div>
        </div>
        <div className="hero-image">
          <div className="floating-card">
            <span className="card-icon">üèÜ</span>
            <h4>Top Rated</h4>
            <p>Best institutes based on {stats.reviews}+ reviews</p>
          </div>
          <div className="floating-card">
            <span className="card-icon">‚≠ê</span>
            <h4>Verified Reviews</h4>
            <p>Authentic feedback from {stats.students}+ students</p>
          </div>
          <div className="floating-card">
            <span className="card-icon">üîç</span>
            <h4>Easy Search</h4>
            <p>Find among {stats.institutes}+ institutes</p>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section">
        <div className="container">
          <div className="stats-grid">
            <div className="stat-item">
              <h3>{stats.institutes}+</h3>
              <p>Verified Institutes</p>
              <small>All institutes are verified and approved</small>
            </div>
            <div className="stat-item">
              <h3>{stats.reviews}+</h3>
              <p>Authentic Reviews</p>
              <small>From real students and parents</small>
            </div>
            <div className="stat-item">
              <h3>{stats.students}+</h3>
              <p>Students Helped</p>
              <small>Making informed decisions</small>
            </div>
            <div className="stat-item">
              <h3>{stats.cities}+</h3>
              <p>Cities Covered</p>
              <small>Across the country</small>
            </div>
          </div>
        </div>
      </section>

      {/* Featured Institutes */}
      <section className="featured-section">
        <div className="container">
          <h2>Featured Institutes</h2>
          <p>Discover some of our top-rated educational institutions based on student reviews</p>
          
          {featuredInstitutes.length === 0 ? (
            <div className="no-institutes">
              <h3>No institutes available yet</h3>
              <p>Be the first to list your institute!</p>
              <Link to="/register" className="btn-primary">
                Register Your Institute
              </Link>
            </div>
          ) : (
            <>
              <div className="institutes-grid">
                {featuredInstitutes.map(institute => (
                  <div key={institute._id} className="institute-card">
                    <div className="card-image">
                      <div className="image-placeholder">
                        {institute.name.charAt(0)}
                      </div>
                      <div className="card-badge">{institute.category}</div>
                      <div className="rating-badge">
                        ‚≠ê {getInstituteRating(institute)}
                      </div>
                    </div>
                    <div className="card-content">
                      <h3>{institute.name}</h3>
                      <p className="affiliation">{institute.affiliation}</p>
                      <p className="location">
                        üìç {institute.city}, {institute.state}
                      </p>
                      
                      {institute.facilities && institute.facilities.length > 0 && (
                        <div className="facilities-preview">
                          {institute.facilities.slice(0, 3).map((facility, index) => (
                            <span key={index} className="facility-tag">
                              {facility}
                            </span>
                          ))}
                          {institute.facilities.length > 3 && (
                            <span className="facility-tag">
                              +{institute.facilities.length - 3} more
                            </span>
                          )}
                        </div>
                      )}
                      
                      <div className="card-footer">
                        <Link 
                          to={`/institutes/${institute._id}`} 
                          className="view-details-btn"
                        >
                          View Details
                        </Link>
                        <span className="reviews-count">
                          üìä {reviews.filter(r => r.institute?._id === institute._id).length} reviews
                        </span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              <div className="view-all-container">
                <Link to="/institutes" className="btn-outline">
                  View All {stats.institutes} Institutes
                </Link>
              </div>
            </>
          )}
        </div>
      </section>

      {/* How It Works */}
      <section className="how-it-works">
        <div className="container">
          <h2>How It Works</h2>
          <div className="steps-grid">
            <div className="step-card">
              <div className="step-number">1</div>
              <h3>Search & Discover</h3>
              <p>Find from {stats.institutes}+ institutes by location, category, fees, and facilities across {stats.cities}+ cities</p>
            </div>
            <div className="step-card">
              <div className="step-number">2</div>
              <h3>Compare & Analyze</h3>
              <p>Read {stats.reviews}+ authentic reviews from {stats.students}+ students and compare multiple options</p>
            </div>
            <div className="step-card">
              <div className="step-number">3</div>
              <h3>Connect & Enroll</h3>
              <p>Send enquiries directly to institutes and make informed decisions with verified information</p>
            </div>
          </div>
        </div>
      </section>

      {/* Call to Action */}
      <section className="cta-section">
        <div className="container">
          <div className="cta-content">
            <h2>Ready to Find Your Perfect Institute?</h2>
            <p>Join {stats.students}+ students who have found their ideal educational path through EduList</p>
            <div className="cta-buttons">
              <Link to="/institutes" className="btn-primary btn-large">
                Start Exploring Now
              </Link>
              <Link to="/register" className="btn-outline btn-large">
                List Your Institute
              </Link>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
