import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { instituteAPI } from '../../services/api';
import './HomePage.css';

const HomePage = () => {
  const [featuredInstitutes, setFeaturedInstitutes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchFeaturedInstitutes();
  }, []);

  const fetchFeaturedInstitutes = async () => {
    try {
      const response = await instituteAPI.getAll();
      const approvedInstitutes = response.data.filter(inst => inst.status === 'approved');
      setFeaturedInstitutes(approvedInstitutes.slice(0, 6));
    } catch (error) {
      console.error('Error fetching institutes:', error);
      // Set some mock data for demonstration
      setFeaturedInstitutes([
        {
          _id: '1',
          name: 'Delhi Public School',
          category: 'School',
          city: 'Delhi',
          state: 'Delhi',
          affiliation: 'CBSE'
        },
        {
          _id: '2',
          name: 'St. Xavier College',
          category: 'College',
          city: 'Mumbai',
          state: 'Maharashtra',
          affiliation: 'University of Mumbai'
        },
        {
          _id: '3',
          name: 'IIT Coaching Center',
          category: 'Coaching Center',
          city: 'Kota',
          state: 'Rajasthan',
          affiliation: 'Premium Education'
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="homepage-loading">
        <div className="loading-spinner"></div>
        <p>Loading educational institutions...</p>
      </div>
    );
  }

  return (
    <div className="homepage">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="container">
          <div className="hero-content">
            <h1>Find Your Perfect Educational Institution</h1>
            <p>Discover, compare, and choose from thousands of schools, colleges, and coaching centers with authentic reviews and ratings.</p>
            <div className="hero-buttons">
              <Link to="/institutes" className="btn-primary">Explore Institutes</Link>
              <Link to="/register" className="btn-secondary">List Your Institute</Link>
            </div>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section">
        <div className="container">
          <div className="stats-grid">
            <div className="stat-item">
              <h3>100+</h3>
              <p>Institutes</p>
            </div>
            <div className="stat-item">
              <h3>500+</h3>
              <p>Reviews</p>
            </div>
            <div className="stat-item">
              <h3>10,000+</h3>
              <p>Students Helped</p>
            </div>
            <div className="stat-item">
              <h3>50+</h3>
              <p>Cities</p>
            </div>
          </div>
        </div>
      </section>

      {/* Featured Institutes */}
      <section className="featured-section">
        <div className="container">
          <h2>Featured Institutes</h2>
          <p>Discover some of our top-rated educational institutions</p>
          
          <div className="institutes-grid">
            {featuredInstitutes.map(institute => (
              <div key={institute._id} className="institute-card">
                <div className="card-image">
                  <div className="image-placeholder">
                    {institute.name.charAt(0)}
                  </div>
                  <div className="card-badge">{institute.category}</div>
                </div>
                <div className="card-content">
                  <h3>{institute.name}</h3>
                  <p className="location">
                    üìç {institute.city}, {institute.state}
                  </p>
                  <p className="affiliation">{institute.affiliation}</p>
                  <div className="card-footer">
                    <Link 
                      to={`/institutes/${institute._id}`} 
                      className="view-details-btn"
                    >
                      View Details
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="view-all-container">
            <Link to="/institutes" className="btn-outline">
              View All Institutes
            </Link>
          </div>
        </div>
      </section>

      {/* How It Works */}
      <section className="how-it-works">
        <div className="container">
          <h2>How It Works</h2>
          <div className="steps-grid">
            <div className="step-card">
              <div className="step-number">1</div>
              <h3>Search & Discover</h3>
              <p>Find institutes by location, category, fees, and facilities</p>
            </div>
            <div className="step-card">
              <div className="step-number">2</div>
              <h3>Compare & Analyze</h3>
              <p>Read authentic reviews and compare multiple options</p>
            </div>
            <div className="step-card">
              <div className="step-number">3</div>
              <h3>Connect & Enroll</h3>
              <p>Send enquiries directly to institutes and make informed decisions</p>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
