import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { useApp } from '../../context/AppContext';
import { instituteAPI, reviewAPI, enquiryAPI } from '../../services/api';
import ReviewForm from './ReviewForm';
import EnquiryForm from './EnquiryForm';
import './UserPanel.css';

const InstituteDetail = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const { addAlert } = useApp();
  const [institute, setInstitute] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [activeTab, setActiveTab] = useState('overview');
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [showEnquiryForm, setShowEnquiryForm] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchInstituteDetails();
  }, [id]);

  const fetchInstituteDetails = async () => {
    try {
      const [instituteRes, reviewsRes] = await Promise.all([
        instituteAPI.getById(id),
        reviewAPI.getByInstitute(id)
      ]);

      setInstitute(instituteRes.data);
      setReviews(reviewsRes.data || []);
    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to load institute details'
      });
    } finally {
      setLoading(false);
    }
  };

  const calculateAverageRating = () => {
    if (reviews.length === 0) return 0;
    const sum = reviews.reduce((total, review) => total + review.rating, 0);
    return (sum / reviews.length).toFixed(1);
  };

  const getRatingDistribution = () => {
    const distribution = { 5: 0, 4: 0, 3: 0, 2: 0, 1: 0 };
    reviews.forEach(review => {
      distribution[review.rating]++;
    });
    return distribution;
  };

  const handleReviewSubmit = async (reviewData) => {
    try {
      await reviewAPI.create({
        ...reviewData,
        institute: id,
        user: user._id
      });

      addAlert({
        type: 'success',
        message: 'Review submitted successfully'
      });

      setShowReviewForm(false);
      fetchInstituteDetails(); // Refresh reviews
    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to submit review'
      });
    }
  };

  const handleEnquirySubmit = async (enquiryData) => {
    try {
      await enquiryAPI.create({
        ...enquiryData,
        institute: id,
        user: user?._id
      });

      addAlert({
        type: 'success',
        message: 'Enquiry submitted successfully'
      });

      setShowEnquiryForm(false);
    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to submit enquiry'
      });
    }
  };

  if (loading) {
    return (
      <div className="user-container">
        <div className="loading">Loading institute details...</div>
      </div>
    );
  }

  if (!institute) {
    return (
      <div className="user-container">
        <div className="error-state">
          <i className="fas fa-exclamation-triangle"></i>
          <h3>Institute not found</h3>
          <p>The institute you're looking for doesn't exist or has been removed.</p>
          <Link to="/institutes" className="btn btn-primary">
            Browse Institutes
          </Link>
        </div>
      </div>
    );
  }

  const avgRating = calculateAverageRating();
  const ratingDistribution = getRatingDistribution();
  const totalReviews = reviews.length;

  return (
    <div className="user-container">
      {/* Institute Header */}
      <div className="institute-header">
        <div className="institute-banner">
          {institute.bannerImage ? (
            <img src={institute.bannerImage} alt={institute.name} />
          ) : (
            <div className="banner-placeholder">
              <i className="fas fa-school"></i>
            </div>
          )}
        </div>

        <div className="institute-profile">
          <div className="profile-logo">
            {institute.logo ? (
              <img src={institute.logo} alt={`${institute.name} logo`} />
            ) : (
              <div className="logo-placeholder">
                {institute.name?.charAt(0)}
              </div>
            )}
          </div>

          <div className="profile-info">
            <h1>{institute.name}</h1>
            <p className="affiliation">{institute.affiliation}</p>
            <p className="location">
              <i className="fas fa-map-marker-alt"></i>
              {institute.address}, {institute.city}, {institute.state}
            </p>

            <div className="profile-stats">
              <div className="stat">
                <div className="rating-badge">
                  <span className="rating-value">{avgRating}</span>
                  <div className="rating-stars">
                    {'★'.repeat(Math.floor(avgRating))}
                    {'☆'.repeat(5 - Math.floor(avgRating))}
                  </div>
                  <span className="review-count">({totalReviews} reviews)</span>
                </div>
              </div>
              <div className="stat">
                <i className="fas fa-phone"></i>
                <span>{institute.contactNumber}</span>
              </div>
              <div className="stat">
                <i className="fas fa-globe"></i>
                <a href={institute.website} target="_blank" rel="noopener noreferrer">
                  Visit Website
                </a>
              </div>
            </div>
          </div>

          <div className="profile-actions">
            <button 
              onClick={() => setShowEnquiryForm(true)}
              className="btn btn-primary"
            >
              <i className="fas fa-envelope"></i>
              Send Enquiry
            </button>
            <button 
              onClick={() => setShowReviewForm(true)}
              className="btn btn-outline"
            >
              <i className="fas fa-star"></i>
              Write Review
            </button>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="institute-tabs">
        <nav className="tab-nav">
          {['overview', 'courses', 'facilities', 'reviews', 'contact'].map(tab => (
            <button
              key={tab}
              className={`tab-button ${activeTab === tab ? 'active' : ''}`}
              onClick={() => setActiveTab(tab)}
            >
              {tab.charAt(0).toUpperCase() + tab.slice(1)}
            </button>
          ))}
        </nav>
      </div>

      {/* Tab Content */}
      <div className="tab-content">
        {activeTab === 'overview' && <OverviewTab institute={institute} />}
        {activeTab === 'courses' && <CoursesTab courses={institute.courses} />}
        {activeTab === 'facilities' && <FacilitiesTab facilities={institute.facilities} />}
        {activeTab === 'reviews' && (
          <ReviewsTab 
            reviews={reviews}
            avgRating={avgRating}
            ratingDistribution={ratingDistribution}
            totalReviews={totalReviews}
          />
        )}
        {activeTab === 'contact' && <ContactTab institute={institute} />}
      </div>

      {/* Modals */}
      {showReviewForm && (
        <ReviewForm
          institute={institute}
          onSubmit={handleReviewSubmit}
          onClose={() => setShowReviewForm(false)}
        />
      )}

      {showEnquiryForm && (
        <EnquiryForm
          institute={institute}
          onSubmit={handleEnquirySubmit}
          onClose={() => setShowEnquiryForm(false)}
        />
      )}
    </div>
  );
};

// Tab Components
const OverviewTab = ({ institute }) => (
  <div className="tab-panel">
    <div className="overview-grid">
      <div className="overview-content">
        <h3>About {institute.name}</h3>
        <p>{institute.description || 'No description available.'}</p>

        <div className="key-details">
          <div className="detail-item">
            <i className="fas fa-calendar-alt"></i>
            <div>
              <strong>Established</strong>
              <span>{institute.establishedYear || 'N/A'}</span>
            </div>
          </div>
          <div className="detail-item">
            <i className="fas fa-users"></i>
            <div>
              <strong>Total Students</strong>
              <span>{institute.totalStudents || 'N/A'}</span>
            </div>
          </div>
          <div className="detail-item">
            <i className="fas fa-chalkboard-teacher"></i>
            <div>
              <strong>Faculty</strong>
              <span>{institute.facultyCount || 'N/A'}</span>
            </div>
          </div>
        </div>
      </div>

      <div className="overview-sidebar">
        <div className="info-card">
          <h4>Quick Info</h4>
          <div className="info-list">
            <div className="info-item">
              <strong>Affiliation:</strong>
              <span>{institute.affiliation}</span>
            </div>
            <div className="info-item">
              <strong>Address:</strong>
              <span>{institute.address}, {institute.city}</span>
            </div>
            <div className="info-item">
              <strong>Contact:</strong>
              <span>{institute.contactNumber}</span>
            </div>
            <div className="info-item">
              <strong>Email:</strong>
              <span>{institute.email}</span>
            </div>
            <div className="info-item">
              <strong>Website:</strong>
              <a href={institute.website} target="_blank" rel="noopener noreferrer">
                {institute.website}
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
);

const CoursesTab = ({ courses }) => (
  <div className="tab-panel">
    <h3>Courses Offered</h3>
    {courses && courses.length > 0 ? (
      <div className="courses-grid">
        {courses.map(course => (
          <div key={course._id} className="course-card">
            <div className="course-image">
              {course.image ? (
                <img src={course.image} alt={course.title} />
              ) : (
                <div className="image-placeholder">
                  <i className="fas fa-book"></i>
                </div>
              )}
            </div>
            <div className="course-content">
              <h4>{course.title}</h4>
              <p className="course-level">{course.level}</p>
              <p className="course-duration">{course.duration}</p>
              <p className="course-fees">₹{course.fees?.toLocaleString()}/year</p>
              <p className="course-description">{course.description}</p>
              {course.eligibility && (
                <div className="course-eligibility">
                  <strong>Eligibility:</strong> {course.eligibility}
                </div>
              )}
              {course.seats && (
                <div className="course-seats">
                  <strong>Seats Available:</strong> {course.seats}
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    ) : (
      <div className="no-data">
        <i className="fas fa-book"></i>
        <p>No courses information available</p>
      </div>
    )}
  </div>
);

const FacilitiesTab = ({ facilities }) => (
  <div className="tab-panel">
    <h3>Facilities & Infrastructure</h3>
    {facilities && facilities.length > 0 ? (
      <div className="facilities-grid">
        {facilities.map(facility => (
          <div key={facility._id} className="facility-card">
            <div className="facility-icon">
              <i className={`fas fa-${getFacilityIcon(facility.type)}`}></i>
            </div>
            <div className="facility-content">
              <h4>{facility.name}</h4>
              <span className={`status-badge ${facility.availability}`}>
                {facility.availability}
              </span>
              <p className="facility-type">{facility.type}</p>
              <p>{facility.description}</p>
              {facility.capacity && (
                <div className="facility-capacity">
                  Capacity: {facility.capacity} people
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    ) : (
      <div className="no-data">
        <i className="fas fa-dumbbell"></i>
        <p>No facilities information available</p>
      </div>
    )}
  </div>
);

const ReviewsTab = ({ reviews, avgRating, ratingDistribution, totalReviews }) => (
  <div className="tab-panel">
    <div className="reviews-header">
      <div className="rating-summary">
        <div className="overall-rating">
          <span className="rating-value">{avgRating}</span>
          <div className="rating-stars-large">
            {'★'.repeat(Math.floor(avgRating))}
            {'☆'.repeat(5 - Math.floor(avgRating))}
          </div>
          <span className="total-reviews">{totalReviews} reviews</span>
        </div>
      </div>

      <div className="rating-distribution">
        {[5, 4, 3, 2, 1].map(rating => (
          <div key={rating} className="distribution-item">
            <span className="rating-label">{rating} star</span>
            <div className="distribution-bar">
              <div 
                className="distribution-fill"
                style={{ 
                  width: totalReviews > 0 ? `${(ratingDistribution[rating] / totalReviews) * 100}%` : '0%' 
                }}
              ></div>
            </div>
            <span className="distribution-count">({ratingDistribution[rating]})</span>
          </div>
        ))}
      </div>
    </div>

    <div className="reviews-list">
      {reviews.length > 0 ? (
        reviews.map(review => (
          <div key={review._id} className="review-card">
            <div className="review-header">
              <div className="reviewer-info">
                <div className="reviewer-avatar">
                  {review.user?.name?.charAt(0) || 'U'}
                </div>
                <div>
                  <h4>{review.user?.name || 'Anonymous'}</h4>
                  <span className="review-date">
                    {new Date(review.createdAt).toLocaleDateString()}
                  </span>
                </div>
              </div>
              <div className="review-rating">
                {'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}
              </div>
            </div>
            <p className="review-text">{review.reviewText}</p>
          </div>
        ))
      ) : (
        <div className="no-data">
          <i className="fas fa-star"></i>
          <p>No reviews yet. Be the first to review this institute!</p>
        </div>
      )}
    </div>
  </div>
);

const ContactTab = ({ institute }) => (
  <div className="tab-panel">
    <div className="contact-grid">
      <div className="contact-info">
        <h3>Contact Information</h3>
        <div className="contact-list">
          <div className="contact-item">
            <i className="fas fa-map-marker-alt"></i>
            <div>
              <strong>Address</strong>
              <p>{institute.address}<br />{institute.city}, {institute.state}</p>
            </div>
          </div>
          <div className="contact-item">
            <i className="fas fa-phone"></i>
            <div>
              <strong>Phone</strong>
              <p>{institute.contactNumber}</p>
            </div>
          </div>
          <div className="contact-item">
            <i className="fas fa-envelope"></i>
            <div>
              <strong>Email</strong>
              <p>{institute.email}</p>
            </div>
          </div>
          <div className="contact-item">
            <i className="fas fa-globe"></i>
            <div>
              <strong>Website</strong>
              <a href={institute.website} target="_blank" rel="noopener noreferrer">
                {institute.website}
              </a>
            </div>
          </div>
        </div>
      </div>

      <div className="map-placeholder">
        <i className="fas fa-map"></i>
        <p>Map integration would show here</p>
        <small>Google Maps API integration</small>
      </div>
    </div>
  </div>
);

// Helper function for facility icons
const getFacilityIcon = (type) => {
  const iconMap = {
    'Library': 'book',
    'Laboratory': 'flask',
    'Sports': 'running',
    'Hostel': 'bed',
    'Cafeteria': 'utensils',
    'Auditorium': 'theater-masks',
    'Transport': 'bus',
    'Medical': 'first-aid',
    'Computer Lab': 'desktop',
    'Playground': 'futbol'
  };
  return iconMap[type] || 'building';
};

export default InstituteDetail;
