import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { useApp } from '../../context/AppContext';
import { adminAPI } from '../../services/api';
import AdminSidebar from './AdminSidebar';
import './AdminPanel.css';

const AdminDashboard = () => {
  const { user } = useAuth();
  const { addAlert, recentActivities } = useApp();
  const [stats, setStats] = useState({
    totalInstitutes: 0,
    pendingInstitutes: 0,
    totalUsers: 0,
    totalReviews: 0,
    totalEnquiries: 0
  });
  const [recentInstitutes, setRecentInstitutes] = useState([]);
  const [recentReviews, setRecentReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, [user]);

  const fetchDashboardData = async () => {
    try {
      const [statsRes, pendingRes, reviewsRes] = await Promise.all([
        adminAPI.getStats(),
        adminAPI.getPendingInstitutes(),
        adminAPI.getAllReviews()
      ]);

      const statsData = statsRes.data;
      const pendingInstitutes = pendingRes.data;
      const allReviews = reviewsRes.data;

      setStats({
        totalInstitutes: statsData.totalInstitutes || 0,
        pendingInstitutes: pendingInstitutes.length || 0,
        totalUsers: statsData.totalUsers || 0,
        totalReviews: statsData.totalReviews || 0,
        totalEnquiries: statsData.totalEnquiries || 0
      });

      // Get recent institutes (last 5 registered)
      setRecentInstitutes(pendingInstitutes.slice(0, 5));
      
      // Get recent reviews (last 5)
      setRecentReviews(allReviews.slice(0, 5));

      // Add recent activities to context
      if (pendingInstitutes.length > 0) {
        pendingInstitutes.forEach(institute => {
          addAlert({
            type: 'warning',
            message: `New institute registration: ${institute.name}`
          });
        });
      }

    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to load dashboard data'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleInstituteAction = async (instituteId, action) => {
    try {
      // This would typically be an API call to update institute status
      addAlert({
        type: 'success',
        message: `Institute ${action} successfully`
      });
      fetchDashboardData(); // Refresh data
    } catch (error) {
      addAlert({
        type: 'error',
        message: `Failed to ${action} institute`
      });
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <AdminSidebar />
        <div className="dashboard-content">
          <div className="loading">Loading dashboard...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <AdminSidebar />
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Admin Dashboard</h1>
          <p>Welcome back, {user?.name}. Here's your platform overview.</p>
        </div>

        {/* Stats Cards */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon primary">
              <i className="fas fa-school"></i>
            </div>
            <div className="stat-info">
              <h3>{stats.totalInstitutes}</h3>
              <p>Total Institutes</p>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon warning">
              <i className="fas fa-clock"></i>
            </div>
            <div className="stat-info">
              <h3>{stats.pendingInstitutes}</h3>
              <p>Pending Approvals</p>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon success">
              <i className="fas fa-users"></i>
            </div>
            <div className="stat-info">
              <h3>{stats.totalUsers}</h3>
              <p>Total Users</p>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-card">
              <div className="stat-icon info">
                <i className="fas fa-star"></i>
              </div>
              <div className="stat-info">
                <h3>{stats.totalReviews}</h3>
                <p>Total Reviews</p>
              </div>
            </div>
          </div>
        </div>

        {/* Recent Activities */}
        <div className="dashboard-grid">
          {/* Pending Institutes */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3>Pending Institute Approvals</h3>
              <Link to="/admin/institutes" className="btn btn-sm btn-primary">
                View All
              </Link>
            </div>
            <div className="card-content">
              {recentInstitutes.length > 0 ? (
                <div className="activity-list">
                  {recentInstitutes.map(institute => (
                    <div key={institute._id} className="activity-item">
                      <div className="activity-info">
                        <h4>{institute.name}</h4>
                        <p>{institute.affiliation} • {institute.city}, {institute.state}</p>
                        <span className="activity-time">
                          Registered: {new Date(institute.createdAt).toLocaleDateString()}
                        </span>
                      </div>
                      <div className="action-buttons">
                        <button 
                          onClick={() => handleInstituteAction(institute._id, 'approve')}
                          className="btn btn-sm btn-success"
                        >
                          Approve
                        </button>
                        <button 
                          onClick={() => handleInstituteAction(institute._id, 'reject')}
                          className="btn btn-sm btn-danger"
                        >
                          Reject
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="no-data">No pending institute approvals</p>
              )}
            </div>
          </div>

          {/* Recent Reviews */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3>Recent Reviews</h3>
              <Link to="/admin/reviews" className="btn btn-sm btn-primary">
                View All
              </Link>
            </div>
            <div className="card-content">
              {recentReviews.length > 0 ? (
                <div className="activity-list">
                  {recentReviews.map(review => (
                    <div key={review._id} className="activity-item">
                      <div className="activity-info">
                        <div className="review-header">
                          <h4>{review.user?.name || 'Anonymous User'}</h4>
                          <div className="rating-stars">
                            {'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}
                          </div>
                        </div>
                        <p className="institute-name">
                          For: {review.institute?.name || 'Unknown Institute'}
                        </p>
                        <p className="review-text">{review.reviewText}</p>
                        <span className="activity-time">
                          {new Date(review.createdAt).toLocaleDateString()}
                        </span>
                      </div>
                      <div className="action-buttons">
                        <button 
                          onClick={() => handleReviewAction(review._id, 'remove')}
                          className="btn btn-sm btn-danger"
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="no-data">No recent reviews</p>
              )}
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="dashboard-card">
          <div className="card-header">
            <h3>Quick Actions</h3>
          </div>
          <div className="card-content">
            <div className="quick-actions">
              <Link to="/admin/institutes" className="action-btn">
                <i className="fas fa-school"></i>
                <span>Manage Institutes</span>
              </Link>
              <Link to="/admin/users" className="action-btn">
                <i className="fas fa-users"></i>
                <span>Manage Users</span>
              </Link>
              <Link to="/admin/analytics" className="action-btn">
                <i className="fas fa-chart-bar"></i>
                <span>View Analytics</span>
              </Link>
              <Link to="/admin/reviews" className="action-btn">
                <i className="fas fa-star"></i>
                <span>Moderate Reviews</span>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

