import React, { useState, useEffect } from 'react';
import { reviewAPI } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const Reviews = () => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  useEffect(() => {
    fetchReviews();
  }, []);

  const fetchReviews = async () => {
    try {
      const response = await reviewAPI.getByInstitute(user.institute._id);
      setReviews(response.data);
    } catch (error) {
      console.error('Error fetching reviews:', error);
    } finally {
      setLoading(false);
    }
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, index) => (
      <span
        key={index}
        className={`star ${index < rating ? 'filled' : ''}`}
      >
        {index < rating ? '⭐' : '☆'}
      </span>
    ));
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: 'numeric',
      month: 'short',
      year: 'numeric'
    });
  };

  const getRatingStats = () => {
    const total = reviews.length;
    if (total === 0) return null;

    const average = reviews.reduce((sum, review) => sum + review.rating, 0) / total;
    const distribution = [0, 0, 0, 0, 0]; // 1-5 stars
    
    reviews.forEach(review => {
      if (review.rating >= 1 && review.rating <= 5) {
        distribution[review.rating - 1]++;
      }
    });

    return { average: average.toFixed(1), distribution, total };
  };

  const stats = getRatingStats();

  if (loading) {
    return <div className="loading">Loading reviews...</div>;
  }

  return (
    <div className="reviews-management">
      <div className="section-header">
        <h2>Student Reviews</h2>
        <p>See what students are saying about your institute</p>
      </div>

      {stats && (
        <div className="reviews-summary">
          <div className="average-rating">
            <div className="rating-number">{stats.average}</div>
            <div className="rating-stars">
              {renderStars(Math.round(parseFloat(stats.average)))}
            </div>
            <div className="rating-count">{stats.total} reviews</div>
          </div>
          
          <div className="rating-distribution">
            {[5, 4, 3, 2, 1].map(stars => (
              <div key={stars} className="distribution-row">
                <span className="stars-label">{stars} star</span>
                <div className="distribution-bar">
                  <div 
                    className="distribution-fill"
                    style={{ 
                      width: `${(stats.distribution[stars-1] / stats.total) * 100}%` 
                    }}
                  ></div>
                </div>
                <span className="distribution-count">
                  {stats.distribution[stars-1]}
                </span>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="reviews-list">
        {reviews.length === 0 ? (
          <div className="empty-state">
            <h3>No Reviews Yet</h3>
            <p>Student reviews will appear here when they rate your institute.</p>
          </div>
        ) : (
          reviews.map(review => (
            <div key={review._id} className="review-card">
              <div className="review-header">
                <div className="reviewer-info">
                  <h4>{review.user.name}</h4>
                  <div className="review-rating">
                    {renderStars(review.rating)}
                  </div>
                </div>
                <span className="review-date">
                  {formatDate(review.createdAt)}
                </span>
              </div>
              
              <div className="review-content">
                <p>{review.reviewText}</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Reviews;
