import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { enquiryAPI, reviewAPI } from '../../services/api';
import './UserPanel.css';

const UserDashboard = () => {
  const { user } = useAuth();
  const [enquiries, setEnquiries] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    try {
      // Mock data for now - replace with actual API calls
      setEnquiries([
        {
          _id: '1',
          institute: {
            _id: '1',
            name: 'Delhi Public School',
            category: 'school'
          },
          message: 'I would like to know about admission process',
          status: 'responded',
          createdAt: new Date(),
          response: {
            message: 'Please visit our website for admission details',
            respondedAt: new Date()
          }
        }
      ]);
      
      setReviews([
        {
          _id: '1',
          institute: {
            _id: '1',
            name: 'Delhi Public School',
            category: 'school'
          },
          rating: 5,
          reviewText: 'Excellent school with great facilities',
          isApproved: true,
          createdAt: new Date()
        }
      ]);
    } catch (error) {
      console.error('Error fetching user data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'status-pending', label: 'Pending' },
      responded: { class: 'status-responded', label: 'Responded' },
      closed: { class: 'status-closed', label: 'Closed' }
    };
    
    const config = statusConfig[status] || statusConfig.pending;
    return <span className={`status-badge ${config.class}`}>{config.label}</span>;
  };

  const renderStars = (rating) => {
    return '‚≠ê'.repeat(rating) + '‚òÜ'.repeat(5 - rating);
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading your dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="user-dashboard">
      <div className="container">
        <div className="dashboard-header">
          <h1>Welcome back, {user?.name}!</h1>
          <p>Here's your activity and interactions with institutes.</p>
        </div>

        <div className="dashboard-stats">
          <div className="stat-card">
            <div className="stat-icon">üì©</div>
            <div className="stat-info">
              <h3>{enquiries.length}</h3>
              <p>Total Enquiries</p>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">‚≠ê</div>
            <div className="stat-info">
              <h3>{reviews.length}</h3>
              <p>Reviews Written</p>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">üè´</div>
            <div className="stat-info">
              <h3>{new Set(enquiries.map(e => e.institute?._id)).size}</h3>
              <p>Institutes Contacted</p>
            </div>
          </div>
        </div>

        <div className="dashboard-content">
          <div className="content-tabs">
            <button 
              className={`tab ${activeTab === 'overview' ? 'active' : ''}`}
              onClick={() => setActiveTab('overview')}
            >
              Overview
            </button>
            <button 
              className={`tab ${activeTab === 'enquiries' ? 'active' : ''}`}
              onClick={() => setActiveTab('enquiries')}
            >
              My Enquiries ({enquiries.length})
            </button>
            <button 
              className={`tab ${activeTab === 'reviews' ? 'active' : ''}`}
              onClick={() => setActiveTab('reviews')}
            >
              My Reviews ({reviews.length})
            </button>
          </div>

          <div className="tab-content">
            {/* Overview Tab */}
            {activeTab === 'overview' && (
              <div className="tab-panel">
                <h2>Recent Activity</h2>
                <div className="activity-feed">
                  {enquiries.slice(0, 3).map(enquiry => (
                    <div key={enquiry._id} className="activity-item">
                      <div className="activity-icon">üì©</div>
                      <div className="activity-content">
                        <p>You sent an enquiry to <strong>{enquiry.institute?.name}</strong></p>
                        <small>{new Date(enquiry.createdAt).toLocaleDateString()}</small>
                      </div>
                      {getStatusBadge(enquiry.status)}
                    </div>
                  ))}
                  
                  {reviews.slice(0, 3).map(review => (
                    <div key={review._id} className="activity-item">
                      <div className="activity-icon">‚≠ê</div>
                      <div className="activity-content">
                        <p>You reviewed <strong>{review.institute?.name}</strong></p>
                        <small>{new Date(review.createdAt).toLocaleDateString()}</small>
                      </div>
                      <span className="review-rating-small">
                        {renderStars(review.rating)}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Enquiries Tab */}
            {activeTab === 'enquiries' && (
              <div className="tab-panel">
                <h2>My Enquiries</h2>
                {enquiries.length > 0 ? (
                  <div className="enquiries-list">
                    {enquiries.map(enquiry => (
                      <div key={enquiry._id} className="enquiry-item">
                        <div className="enquiry-header">
                          <div className="enquiry-info">
                            <h3>
                              <Link to={`/institute/${enquiry.institute?._id}`}>
                                {enquiry.institute?.name}
                              </Link>
                            </h3>
                            <p className="institute-category">{enquiry.institute?.category}</p>
                            <p className="enquiry-date">
                              Sent on {new Date(enquiry.createdAt).toLocaleDateString()}
                            </p>
                          </div>
                          <div className="enquiry-status">
                            {getStatusBadge(enquiry.status)}
                          </div>
                        </div>
                        
                        <div className="enquiry-content">
                          <p><strong>Your Message:</strong> {enquiry.message}</p>
                          
                          {enquiry.response && (
                            <div className="enquiry-response">
                              <strong>Institute's Response:</strong>
                              <p>{enquiry.response.message}</p>
                              <small>
                                Responded on: {new Date(enquiry.response.respondedAt).toLocaleString()}
                              </small>
                            </div>
                          )}
                        </div>

                        <div className="enquiry-actions">
                          <Link 
                            to={`/institute/${enquiry.institute?._id}`}
                            className="btn btn-outline"
                          >
                            View Institute
                          </Link>
                          {enquiry.status === 'pending' && (
                            <button className="btn btn-secondary" disabled>
                              Waiting for response
                            </button>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="empty-state">
                    <div className="empty-icon">üì©</div>
                    <h3>No Enquiries Yet</h3>
                    <p>You haven't sent any enquiries to institutes yet.</p>
                    <Link to="/institutes" className="btn btn-primary">
                      Browse Institutes
                    </Link>
                  </div>
                )}
              </div>
            )}

            {/* Reviews Tab */}
            {activeTab === 'reviews' && (
              <div className="tab-panel">
                <h2>My Reviews</h2>
                {reviews.length > 0 ? (
                  <div className="reviews-list">
                    {reviews.map(review => (
                      <div key={review._id} className="review-item">
                        <div className="review-header">
                          <div className="review-info">
                            <h3>
                              <Link to={`/institute/${review.institute?._id}`}>
                                {review.institute?.name}
                              </Link>
                            </h3>
                            <p className="institute-category">{review.institute?.category}</p>
                            <div className="review-rating">
                              {renderStars(review.rating)}
                            </div>
                            <p className="review-date">
                              Reviewed on {new Date(review.createdAt).toLocaleDateString()}
                            </p>
                          </div>
                          <div className="review-status">
                            <span className={`status-badge ${review.isApproved ? 'status-active' : 'status-pending'}`}>
                              {review.isApproved ? 'Approved' : 'Pending Approval'}
                            </span>
                          </div>
                        </div>
                        
                        <div className="review-content">
                          <p>{review.reviewText}</p>
                        </div>

                        <div className="review-actions">
                          <Link 
                            to={`/institute/${review.institute?._id}`}
                            className="btn btn-outline"
                          >
                            View Institute
                          </Link>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="empty-state">
                    <div className="empty-icon">‚≠ê</div>
                    <h3>No Reviews Yet</h3>
                    <p>You haven't written any reviews for institutes yet.</p>
                    <Link to="/institutes" className="btn btn-primary">
                      Browse Institutes
                    </Link>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        <div className="dashboard-actions">
          <h3>Quick Actions</h3>
          <div className="action-buttons">
            <Link to="/institutes" className="action-card">
              <span className="action-icon">üîç</span>
              <span className="action-text">Browse Institutes</span>
            </Link>
            <Link to="/institutes" className="action-card">
              <span className="action-icon">‚≠ê</span>
              <span className="action-text">Write a Review</span>
            </Link>
            <Link to="/institutes" className="action-card">
              <span className="action-icon">üìß</span>
              <span className="action-text">Send Enquiry</span>
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
