import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import { toast } from '../../utils/toast';
import './AdminPanel.css';

const ManageInstitutes = () => {
  const [institutes, setInstitutes] = useState([]);
  const [pendingInstitutes, setPendingInstitutes] = useState([]);
  const [activeTab, setActiveTab] = useState('pending');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchInstitutes();
  }, []);

  const fetchInstitutes = async () => {
    try {
      setLoading(true);
      console.log('üìã Fetching institutes data...');
      
      // Fetch all institutes
      const allResponse = await api.get('/api/admin/institutes');
      console.log('All institutes:', allResponse.data);
      
      // Fetch pending institutes
      const pendingResponse = await api.get('/api/admin/institutes/pending');
      console.log('Pending institutes:', pendingResponse.data);
      
      setInstitutes(allResponse.data);
      setPendingInstitutes(pendingResponse.data);
      
    } catch (error) {
      console.error('‚ùå Error fetching institutes:', error);
      toast.error('Failed to load institutes');
    } finally {
      setLoading(false);
    }
  };

  const handleApproveReject = async (instituteId, action) => {
    try {
      console.log(`üîÑ ${action} institute:`, instituteId);
      
      await api.patch(`/api/admin/institutes/${instituteId}/approve`, { action });
      
      if (action === 'approve') {
        toast.success('Institute approved successfully!');
      } else {
        toast.success('Institute rejected successfully!');
      }
      
      // Refresh data
      fetchInstitutes();
    } catch (error) {
      console.error(`Error ${action}ing institute:`, error);
      toast.error(`Failed to ${action} institute`);
    }
  };

  const handleStatusChange = async (instituteId, isActive) => {
    try {
      await api.patch(`/api/admin/institutes/${instituteId}/status`, { isActive });
      
      toast.success(`Institute ${isActive ? 'activated' : 'deactivated'} successfully!`);
      fetchInstitutes();
    } catch (error) {
      toast.error('Failed to update institute status');
    }
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading institutes...</p>
      </div>
    );
  }

  return (
    <div className="manage-institutes">
      <div className="section-header">
        <h2>Manage Institutes</h2>
        <p>Approve new institutes and manage existing ones</p>
        
        <div className="stats-overview">
          <div className="stat-item">
            <div className="stat-number">{pendingInstitutes.length}</div>
            <div className="stat-label">Pending Approval</div>
          </div>
          <div className="stat-item">
            <div className="stat-number">{institutes.length}</div>
            <div className="stat-label">Total Institutes</div>
          </div>
        </div>
      </div>

      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'pending' ? 'active' : ''}`}
          onClick={() => setActiveTab('pending')}
        >
          Pending Approval ({pendingInstitutes.length})
        </button>
        <button 
          className={`tab ${activeTab === 'all' ? 'active' : ''}`}
          onClick={() => setActiveTab('all')}
        >
          All Institutes ({institutes.length})
        </button>
      </div>

      <div className="tab-content">
        {activeTab === 'pending' ? (
          <PendingInstitutes 
            institutes={pendingInstitutes}
            onApproveReject={handleApproveReject}
          />
        ) : (
          <AllInstitutes 
            institutes={institutes}
            onStatusChange={handleStatusChange}
          />
        )}
      </div>
    </div>
  );
};

const PendingInstitutes = ({ institutes, onApproveReject }) => {
  if (institutes.length === 0) {
    return (
      <div className="empty-state">
        <h3>No pending approvals</h3>
        <p>All institute registrations have been processed.</p>
        <div className="empty-illustration">üìã</div>
      </div>
    );
  }

  return (
    <div className="institutes-list">
      {institutes.map((institute) => (
        <div key={institute._id} className="institute-card pending">
          <div className="institute-info">
            <div className="institute-header">
              <h3>{institute.name}</h3>
              <span className="status-badge pending">Pending Approval</span>
            </div>
            
            <div className="institute-details">
              <div className="detail-row">
                <span className="label">Category:</span>
                <span className="value">{institute.category}</span>
              </div>
              <div className="detail-row">
                <span className="label">Affiliation:</span>
                <span className="value">{institute.affiliation}</span>
              </div>
              <div className="detail-row">
                <span className="label">Location:</span>
                <span className="value">{institute.address.city}, {institute.address.state}</span>
              </div>
              <div className="detail-row">
                <span className="label">Contact:</span>
                <span className="value">{institute.user?.email} | {institute.user?.phone}</span>
              </div>
            </div>

            <div className="institute-description">
              <strong>Description:</strong>
              <p>{institute.description}</p>
            </div>

            <div className="application-info">
              <small>
                Applied on: {new Date(institute.createdAt).toLocaleDateString()} at{' '}
                {new Date(institute.createdAt).toLocaleTimeString()}
              </small>
            </div>
          </div>
          
          <div className="institute-actions">
            <button 
              onClick={() => onApproveReject(institute._id, 'approve')}
              className="btn btn-success btn-block"
            >
              ‚úÖ Approve Institute
            </button>
            <button 
              onClick={() => onApproveReject(institute._id, 'reject')}
              className="btn btn-danger btn-block"
            >
              ‚ùå Reject Institute
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

const AllInstitutes = ({ institutes, onStatusChange }) => {
  return (
    <div className="table-container">
      <table className="table">
        <thead>
          <tr>
            <th>Institute Name</th>
            <th>Category</th>
            <th>Location</th>
            <th>Status</th>
            <th>Rating</th>
            <th>Registration Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {institutes.map((institute) => (
            <tr key={institute._id}>
              <td>
                <div className="institute-name">
                  <strong>{institute.name}</strong>
                  <div className="affiliation">{institute.affiliation}</div>
                </div>
              </td>
              <td>
                <span className="category-tag">{institute.category}</span>
              </td>
              <td>
                {institute.address.city}, {institute.address.state}
              </td>
              <td>
                <span className={`status-badge ${institute.isVerified ? 'verified' : 'pending'} ${institute.isActive ? 'active' : 'inactive'}`}>
                  {institute.isVerified ? (institute.isActive ? 'Active' : 'Inactive') : 'Pending'}
                </span>
              </td>
              <td>
                <div className="rating">
                  ‚≠ê {institute.rating || 'N/A'}
                  <div className="review-count">({institute.reviewCount} reviews)</div>
                </div>
              </td>
              <td>
                {new Date(institute.createdAt).toLocaleDateString()}
              </td>
              <td>
                <div className="action-buttons">
                  {institute.isVerified ? (
                    institute.isActive ? (
                      <button 
                        onClick={() => onStatusChange(institute._id, false)}
                        className="btn btn-sm btn-warning"
                      >
                        Deactivate
                      </button>
                    ) : (
                      <button 
                        onClick={() => onStatusChange(institute._id, true)}
                        className="btn btn-sm btn-success"
                      >
                        Activate
                      </button>
                    )
                  ) : (
                    <span className="text-muted">Pending Approval</span>
                  )}
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageInstitutes;
