import React, { useState, useEffect } from 'react';
import { useParams, Navigate } from 'react-router-dom';
import { instituteAPI, reviewAPI, courseAPI, facilitiesAPI, enquiryAPI } from '../../services/api';
import { useAuth } from '../../context/AuthContext';
import './UserPanel.css';

const InstituteDetail = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const [institute, setInstitute] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [courses, setCourses] = useState([]);
  const [facilities, setFacilities] = useState([]);
  const [activeTab, setActiveTab] = useState('overview');
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [showEnquiryForm, setShowEnquiryForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  const [reviewForm, setReviewForm] = useState({
    rating: 5,
    reviewText: ''
  });
  
  const [enquiryForm, setEnquiryForm] = useState({
    name: user?.name || '',
    email: user?.email || '',
    phone: '',
    message: ''
  });

  useEffect(() => {
    // Check if the ID is a valid MongoDB ObjectId (24 hex characters)
    const isValidObjectId = /^[0-9a-fA-F]{24}$/.test(id);
    
    if (!isValidObjectId) {
      setError('Invalid institute ID');
      setLoading(false);
      return;
    }

    fetchInstituteData();
  }, [id]);

  const fetchInstituteData = async () => {
    try {
      console.log('Fetching institute data for ID:', id);
      
      const [instituteRes, reviewsRes, coursesRes, facilitiesRes] = await Promise.all([
        instituteAPI.getById(id),
        reviewAPI.getByInstitute(id),
        courseAPI.getByInstitute(id),
        facilitiesAPI.getByInstitute(id)
      ]);

      console.log('Institute data:', instituteRes.data);
      console.log('Reviews data:', reviewsRes.data);
      console.log('Courses data:', coursesRes.data);
      console.log('Facilities data:', facilitiesRes.data);

      setInstitute(instituteRes.data);
      setReviews(reviewsRes.data);
      setCourses(coursesRes.data);
      setFacilities(facilitiesRes.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching institute data:', error);
      setError('Failed to load institute details');
      setLoading(false);
    }
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    if (!user) {
      alert('Please login to submit a review');
      return;
    }

    try {
      await reviewAPI.create({
        institute: id,
        ...reviewForm
      });
      setShowReviewForm(false);
      setReviewForm({ rating: 5, reviewText: '' });
      fetchInstituteData(); // Refresh reviews
    } catch (error) {
      console.error('Error submitting review:', error);
      alert('Error submitting review. Please try again.');
    }
  };

  const handleEnquirySubmit = async (e) => {
    e.preventDefault();
    try {
      await enquiryAPI.create({
        institute: id,
        ...enquiryForm
      });
      setShowEnquiryForm(false);
      setEnquiryForm({
        name: user?.name || '',
        email: user?.email || '',
        phone: '',
        message: ''
      });
      alert('Enquiry submitted successfully!');
    } catch (error) {
      console.error('Error submitting enquiry:', error);
      alert('Error submitting enquiry. Please try again.');
    }
  };

  const renderStars = (rating) => {
    return (
      <div className="star-rating">
        {[1, 2, 3, 4, 5].map((star) => (
          <span
            key={star}
            className={star <= rating ? 'star filled' : 'star'}
          >
            ‚≠ê
          </span>
        ))}
      </div>
    );
  };

  const calculateAverageRating = () => {
    if (reviews.length === 0) return 0;
    const total = reviews.reduce((sum, review) => sum + review.rating, 0);
    return (total / reviews.length).toFixed(1);
  };

  if (loading) {
    return <div className="loading">Loading institute details...</div>;
  }

  if (error) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={() => window.history.back()} className="btn-primary">
          Go Back
        </button>
      </div>
    );
  }

  if (!institute) {
    return <Navigate to="/institutes" replace />;
  }

  return (
    <div className="institute-detail">
      {/* Header Section */}
      <div className="institute-header">
        <div className="institute-hero">
          <div className="hero-content">
            <h1>{institute.name}</h1>
            <p className="affiliation">{institute.affiliation}</p>
            <p className="location">
              üìç {institute.address}, {institute.city}, {institute.state}
            </p>
            
            <div className="rating-section">
              <div className="average-rating">
                <span className="rating-number">{calculateAverageRating()}</span>
                {renderStars(calculateAverageRating())}
                <span className="rating-count">({reviews.length} reviews)</span>
              </div>
            </div>

            <div className="action-buttons">
              <button 
                onClick={() => setShowEnquiryForm(true)}
                className="btn-primary"
              >
                Send Enquiry
              </button>
              {user && user.role === 'user' && (
                <button 
                  onClick={() => setShowReviewForm(true)}
                  className="btn-outline"
                >
                  Write Review
                </button>
              )}
            </div>
          </div>
          
          <div className="hero-sidebar">
            <div className="contact-card">
              <h3>Contact Information</h3>
              <p>üìû {institute.phone}</p>
              <p>üìß {institute.email}</p>
              {institute.website && <p>üåê {institute.website}</p>}
              <p>üè¢ {institute.category}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="detail-tabs">
        <button 
          className={activeTab === 'overview' ? 'active' : ''}
          onClick={() => setActiveTab('overview')}
        >
          Overview
        </button>
        <button 
          className={activeTab === 'courses' ? 'active' : ''}
          onClick={() => setActiveTab('courses')}
        >
          Courses ({courses.length})
        </button>
        <button 
          className={activeTab === 'facilities' ? 'active' : ''}
          onClick={() => setActiveTab('facilities')}
        >
          Facilities ({facilities.length})
        </button>
        <button 
          className={activeTab === 'reviews' ? 'active' : ''}
          onClick={() => setActiveTab('reviews')}
        >
          Reviews ({reviews.length})
        </button>
      </div>

      {/* Tab Content */}
      <div className="tab-content">
        {activeTab === 'overview' && (
          <div className="overview-tab">
            <div className="description-section">
              <h3>About {institute.name}</h3>
              <p>{institute.description}</p>
            </div>

            {institute.facilities && institute.facilities.length > 0 && (
              <div className="facilities-preview">
                <h3>Key Facilities</h3>
                <div className="facilities-tags">
                  {institute.facilities.slice(0, 8).map((facility, index) => (
                    <span key={index} className="facility-tag">
                      {facility}
                    </span>
                  ))}
                  {institute.facilities.length > 8 && (
                    <span className="facility-tag">
                      +{institute.facilities.length - 8} more
                    </span>
                  )}
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'courses' && (
          <div className="courses-tab">
            <h3>Available Courses</h3>
            {courses.length === 0 ? (
              <p className="no-data">No courses available at the moment.</p>
            ) : (
              <div className="courses-grid">
                {courses.map(course => (
                  <div key={course._id} className="course-card">
                    <h4>{course.title}</h4>
                    <p className="course-category">{course.category}</p>
                    <p className="course-duration">‚è±Ô∏è {course.duration}</p>
                    <p className="course-fees">üí∞ ‚Çπ{course.fees?.toLocaleString()}</p>
                    <p className="course-description">{course.description}</p>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'facilities' && (
          <div className="facilities-tab">
            <h3>Campus Facilities</h3>
            {facilities.length === 0 ? (
              <p className="no-data">No facilities information available.</p>
            ) : (
              <div className="facilities-grid-detailed">
                {facilities.map(facility => (
                  <div key={facility._id} className="facility-card-detailed">
                    <span className="facility-icon">{facility.icon}</span>
                    <div className="facility-info">
                      <h4>{facility.name}</h4>
                      <p>{facility.description}</p>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'reviews' && (
          <div className="reviews-tab">
            <h3>Student Reviews</h3>
            {reviews.length === 0 ? (
              <p className="no-data">No reviews yet. Be the first to review!</p>
            ) : (
              <div className="reviews-list-detailed">
                {reviews.map(review => (
                  <div key={review._id} className="review-card-detailed">
                    <div className="review-header">
                      <h4>{review.user?.name || 'Anonymous'}</h4>
                      {renderStars(review.rating)}
                    </div>
                    <p className="review-text">{review.reviewText}</p>
                    <span className="review-date">
                      {new Date(review.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>

      {/* Review Modal */}
      {showReviewForm && (
        <div className="modal">
          <div className="modal-content">
            <h3>Write a Review</h3>
            <form onSubmit={handleReviewSubmit}>
              <div className="form-group">
                <label>Rating</label>
                <div className="rating-input">
                  {[1, 2, 3, 4, 5].map(star => (
                    <button
                      key={star}
                      type="button"
                      className={star <= reviewForm.rating ? 'star-btn active' : 'star-btn'}
                      onClick={() => setReviewForm(prev => ({ ...prev, rating: star }))}
                    >
                      ‚≠ê
                    </button>
                  ))}
                </div>
              </div>
              
              <div className="form-group">
                <label>Your Review</label>
                <textarea
                  value={reviewForm.reviewText}
                  onChange={(e) => setReviewForm(prev => ({ ...prev, reviewText: e.target.value }))}
                  rows="4"
                  required
                />
              </div>
              
              <div className="form-actions">
                <button type="submit" className="btn-primary">Submit Review</button>
                <button 
                  type="button" 
                  onClick={() => setShowReviewForm(false)}
                  className="btn-outline"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Enquiry Modal */}
      {showEnquiryForm && (
        <div className="modal">
          <div className="modal-content">
            <h3>Send Enquiry to {institute.name}</h3>
            <form onSubmit={handleEnquirySubmit}>
              <div className="form-grid">
                <div className="form-group">
                  <label>Full Name *</label>
                  <input
                    type="text"
                    value={enquiryForm.name}
                    onChange={(e) => setEnquiryForm(prev => ({ ...prev, name: e.target.value }))}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <label>Email *</label>
                  <input
                    type="email"
                    value={enquiryForm.email}
                    onChange={(e) => setEnquiryForm(prev => ({ ...prev, email: e.target.value }))}
                    required
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label>Phone Number *</label>
                <input
                  type="tel"
                  value={enquiryForm.phone}
                  onChange={(e) => setEnquiryForm(prev => ({ ...prev, phone: e.target.value }))}
                  required
                />
              </div>
              
              <div className="form-group">
                <label>Message *</label>
                <textarea
                  value={enquiryForm.message}
                  onChange={(e) => setEnquiryForm(prev => ({ ...prev, message: e.target.value }))}
                  rows="4"
                  placeholder="Tell us about your requirements, questions, or any specific information you need..."
                  required
                />
              </div>
              
              <div className="form-actions">
                <button type="submit" className="btn-primary">Send Enquiry</button>
                <button 
                  type="button" 
                  onClick={() => setShowEnquiryForm(false)}
                  className="btn-outline"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default InstituteDetail;
