import React, { useState, useEffect } from 'react';
import { instituteAPI, userAPI } from '../../services/api';

const ManageInstitutes = () => {
  const [institutes, setInstitutes] = useState([]);
  const [pendingInstitutes, setPendingInstitutes] = useState([]);
  const [activeTab, setActiveTab] = useState('pending');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchInstitutes();
    fetchPendingInstitutes();
  }, []);

  const fetchInstitutes = async () => {
    try {
      const response = await instituteAPI.getAll();
      setInstitutes(response.data);
    } catch (error) {
      console.error('Error fetching institutes:', error);
      setMessage('Error fetching institutes');
    }
  };

  const fetchPendingInstitutes = async () => {
    try {
      const response = await instituteAPI.getPending();
      setPendingInstitutes(response.data);
    } catch (error) {
      console.error('Error fetching pending institutes:', error);
      setMessage('Error fetching pending institutes');
    }
  };

  const handleStatusUpdate = async (instituteId, status) => {
    setLoading(true);
    try {
      await instituteAPI.updateStatus(instituteId, status);
      
      // Show success message
      setMessage(`Institute ${status} successfully!`);
      setTimeout(() => setMessage(''), 3000);
      
      // Refresh data
      fetchPendingInstitutes();
      fetchInstitutes();
    } catch (error) {
      console.error('Error updating institute status:', error);
      setMessage('Error updating institute status');
    } finally {
      setLoading(false);
    }
  };

  const handleUserStatusUpdate = async (userId, isActive) => {
    try {
      await userAPI.updateStatus(userId, isActive);
      fetchInstitutes(); // Refresh to update status
      setMessage(`User ${isActive ? 'activated' : 'deactivated'} successfully!`);
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      console.error('Error updating user status:', error);
      setMessage('Error updating user status');
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'status-pending', text: 'Pending' },
      approved: { class: 'status-approved', text: 'Approved' },
      rejected: { class: 'status-rejected', text: 'Rejected' }
    };
    
    const config = statusConfig[status] || statusConfig.pending;
    return <span className={`status-badge ${config.class}`}>{config.text}</span>;
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <div className="manage-institutes">
      <div className="page-header">
        <h1>Manage Institutes</h1>
        <p>Approve, reject, and manage educational institutions</p>
        
        {message && (
          <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
            {message}
          </div>
        )}

        <div className="tab-navigation">
          <button 
            className={activeTab === 'pending' ? 'active' : ''}
            onClick={() => setActiveTab('pending')}
          >
            Pending Approval ({pendingInstitutes.length})
          </button>
          <button 
            className={activeTab === 'all' ? 'active' : ''}
            onClick={() => setActiveTab('all')}
          >
            All Institutes ({institutes.length})
          </button>
          <button 
            className={activeTab === 'approved' ? 'active' : ''}
            onClick={() => setActiveTab('approved')}
          >
            Approved ({institutes.filter(inst => inst.status === 'approved').length})
          </button>
        </div>
      </div>

      {activeTab === 'pending' && (
        <div className="institutes-list">
          <h2>Pending Institute Approvals</h2>
          {pendingInstitutes.length === 0 ? (
            <div className="no-data">
              <div className="no-data-icon">‚úÖ</div>
              <h3>No pending institutes</h3>
              <p>All institute registrations have been processed</p>
            </div>
          ) : (
            pendingInstitutes.map(institute => (
              <div key={institute._id} className="institute-card pending">
                <div className="institute-info">
                  <div className="institute-header">
                    <h3>{institute.name}</h3>
                    {getStatusBadge(institute.status)}
                  </div>
                  
                  <div className="institute-details">
                    <div className="detail-group">
                      <p><strong>Category:</strong> {institute.category}</p>
                      <p><strong>Affiliation:</strong> {institute.affiliation}</p>
                      <p><strong>Address:</strong> {institute.address}, {institute.city}, {institute.state}</p>
                    </div>
                    
                    <div className="detail-group">
                      <p><strong>Contact:</strong> {institute.phone} | {institute.email}</p>
                      {institute.website && <p><strong>Website:</strong> {institute.website}</p>}
                      <p><strong>Registered:</strong> {formatDate(institute.createdAt)}</p>
                    </div>
                  </div>

                  <div className="institute-description">
                    <strong>Description:</strong>
                    <p>{institute.description}</p>
                  </div>

                  {institute.facilities && institute.facilities.length > 0 && (
                    <div className="institute-facilities">
                      <strong>Facilities:</strong>
                      <div className="facilities-list">
                        {institute.facilities.map((facility, index) => (
                          <span key={index} className="facility-tag">
                            {facility}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}

                  <div className="user-info">
                    <strong>Registered by:</strong>
                    <p>{institute.user?.name} ({institute.user?.email})</p>
                  </div>
                </div>
                
                <div className="action-buttons">
                  <button 
                    onClick={() => handleStatusUpdate(institute._id, 'approved')}
                    className="btn-approve"
                    disabled={loading}
                  >
                    {loading ? 'Processing...' : 'Approve Institute'}
                  </button>
                  <button 
                    onClick={() => handleStatusUpdate(institute._id, 'rejected')}
                    className="btn-reject"
                    disabled={loading}
                  >
                    {loading ? 'Processing...' : 'Reject Institute'}
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      )}

      {activeTab === 'all' && (
        <div className="institutes-list">
          <h2>All Institutes</h2>
          {institutes.length === 0 ? (
            <div className="no-data">
              <div className="no-data-icon">üè´</div>
              <h3>No institutes found</h3>
              <p>There are no institutes registered on the platform</p>
            </div>
          ) : (
            institutes.map(institute => (
              <div key={institute._id} className="institute-card">
                <div className="institute-info">
                  <div className="institute-header">
                    <h3>{institute.name}</h3>
                    <div className="status-section">
                      {getStatusBadge(institute.status)}
                      <span className="user-status">
                        User: {institute.user?.isActive ? 'Active' : 'Inactive'}
                      </span>
                    </div>
                  </div>
                  
                  <div className="institute-details">
                    <p><strong>Category:</strong> {institute.category}</p>
                    <p><strong>City:</strong> {institute.city}</p>
                    <p><strong>Contact:</strong> {institute.phone}</p>
                    <p><strong>Email:</strong> {institute.email}</p>
                    <p><strong>Registered:</strong> {formatDate(institute.createdAt)}</p>
                  </div>
                </div>
                
                <div className="action-buttons">
                  {institute.status === 'approved' ? (
                    <>
                      <button 
                        onClick={() => handleStatusUpdate(institute._id, 'rejected')}
                        className="btn-reject"
                      >
                        Deactivate
                      </button>
                      {institute.user && (
                        <button 
                          onClick={() => handleUserStatusUpdate(institute.user._id, !institute.user.isActive)}
                          className={institute.user.isActive ? 'btn-warning' : 'btn-success'}
                        >
                          {institute.user.isActive ? 'Deactivate User' : 'Activate User'}
                        </button>
                      )}
                    </>
                  ) : (
                    <button 
                      onClick={() => handleStatusUpdate(institute._id, 'approved')}
                      className="btn-approve"
                    >
                      Activate
                    </button>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      )}

      {activeTab === 'approved' && (
        <div className="institutes-list">
          <h2>Approved Institutes</h2>
          {institutes.filter(inst => inst.status === 'approved').length === 0 ? (
            <div className="no-data">
              <div className="no-data-icon">‚úÖ</div>
              <h3>No approved institutes</h3>
              <p>No institutes have been approved yet</p>
            </div>
          ) : (
            institutes
              .filter(inst => inst.status === 'approved')
              .map(institute => (
                <div key={institute._id} className="institute-card approved">
                  <div className="institute-info">
                    <div className="institute-header">
                      <h3>{institute.name}</h3>
                      <div className="status-section">
                        {getStatusBadge(institute.status)}
                        <span className="verified-badge">‚úÖ Verified</span>
                      </div>
                    </div>
                    
                    <div className="institute-details">
                      <p><strong>Category:</strong> {institute.category}</p>
                      <p><strong>Location:</strong> {institute.city}, {institute.state}</p>
                      <p><strong>Contact:</strong> {institute.phone}</p>
                      <p><strong>Email:</strong> {institute.email}</p>
                      {institute.website && <p><strong>Website:</strong> {institute.website}</p>}
                    </div>

                    <div className="institute-stats">
                      <div className="stat">
                        <strong>Courses:</strong> <span>--</span>
                      </div>
                      <div className="stat">
                        <strong>Reviews:</strong> <span>--</span>
                      </div>
                      <div className="stat">
                        <strong>Enquiries:</strong> <span>--</span>
                      </div>
                    </div>
                  </div>
                  
                  <div className="action-buttons">
                    <button 
                      onClick={() => handleStatusUpdate(institute._id, 'rejected')}
                      className="btn-reject"
                    >
                      Deactivate
                    </button>
                    <button className="btn-outline">
                      View Details
                    </button>
                  </div>
                </div>
              ))
          )}
        </div>
      )}
    </div>
  );
};

export default ManageInstitutes;
