import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import './UserPanel.css';

const EnquiryForm = ({ institute, onSubmit, onClose }) => {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    name: user?.name || '',
    email: user?.email || '',
    phone: '',
    courseInterest: '',
    message: '',
    preferredContact: 'email'
  });
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.name.trim() || !formData.email.trim() || !formData.message.trim()) {
      alert('Please fill in all required fields');
      return;
    }

    setSubmitting(true);
    await onSubmit(formData);
    setSubmitting(false);
  };

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content enquiry-modal">
        <div className="modal-header">
          <h3>Send Enquiry</h3>
          <button onClick={onClose} className="btn btn-close">
            <i className="fas fa-times"></i>
          </button>
        </div>

        <div className="modal-body">
          <div className="enquiry-institute">
            <div className="institute-logo-sm">
              {institute.logo ? (
                <img src={institute.logo} alt={institute.name} />
              ) : (
                <div className="logo-placeholder-sm">
                  {institute.name?.charAt(0)}
                </div>
              )}
            </div>
            <div>
              <h4>{institute.name}</h4>
              <p>Your enquiry will be sent directly to the institute</p>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="enquiry-form">
            <div className="form-grid">
              <div className="form-group">
                <label className="form-label">Full Name *</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  className="form-control"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Email *</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="form-control"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Phone Number</label>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  className="form-control"
                />
              </div>

              <div className="form-group">
                <label className="form-label">Course Interest</label>
                <select
                  name="courseInterest"
                  value={formData.courseInterest}
                  onChange={handleChange}
                  className="form-control"
                >
                  <option value="">Select a course</option>
                  {institute.courses?.map(course => (
                    <option key={course._id} value={course.title}>
                      {course.title}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group full-width">
                <label className="form-label">Preferred Contact Method</label>
                <div className="radio-group">
                  <label className="radio-label">
                    <input
                      type="radio"
                      name="preferredContact"
                      value="email"
                      checked={formData.preferredContact === 'email'}
                      onChange={handleChange}
                    />
                    <span className="radiomark"></span>
                    Email
                  </label>
                  <label className="radio-label">
                    <input
                      type="radio"
                      name="preferredContact"
                      value="phone"
                      checked={formData.preferredContact === 'phone'}
                      onChange={handleChange}
                    />
                    <span className="radiomark"></span>
                    Phone
                  </label>
                  <label className="radio-label">
                    <input
                      type="radio"
                      name="preferredContact"
                      value="both"
                      checked={formData.preferredContact === 'both'}
                      onChange={handleChange}
                    />
                    <span className="radiomark"></span>
                    Both
                  </label>
                </div>
              </div>

              <div className="form-group full-width">
                <label className="form-label">Your Message *</label>
                <textarea
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  className="form-control"
                  rows="5"
                  placeholder="Please provide details about your enquiry, including any specific questions you have about admission, courses, fees, etc."
                  required
                />
              </div>
            </div>

            <div className="form-actions">
              <button 
                type="button" 
                onClick={onClose}
                className="btn btn-outline"
              >
                Cancel
              </button>
              <button 
                type="submit" 
                disabled={submitting}
                className="btn btn-primary"
              >
                {submitting ? 'Sending...' : 'Send Enquiry'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EnquiryForm;
