import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useApp } from '../../context/AppContext';
import { reviewAPI } from '../../services/api';
import InstituteSidebar from './InstituteSidebar';
import './InstituteDashboard.css';

const Reviews = () => {
  const { user } = useAuth();
  const { addAlert } = useApp();
  const [reviews, setReviews] = useState([]);
  const [filteredReviews, setFilteredReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [ratingFilter, setRatingFilter] = useState('all');

  useEffect(() => {
    fetchReviews();
  }, [user]);

  useEffect(() => {
    filterReviews();
  }, [reviews, ratingFilter]);

  const fetchReviews = async () => {
    try {
      const response = await reviewAPI.getByInstitute(user.instituteId);
      setReviews(response.data || []);
    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to load reviews'
      });
    } finally {
      setLoading(false);
    }
  };

  const filterReviews = () => {
    let filtered = reviews;
    
    if (ratingFilter !== 'all') {
      filtered = filtered.filter(review => review.rating === parseInt(ratingFilter));
    }
    
    setFilteredReviews(filtered);
  };

  const handleFlagReview = async (reviewId) => {
    try {
      // This would typically be an API call to flag review
      addAlert({
        type: 'warning',
        message: 'Review has been flagged for admin review'
      });

      // Update local state to show review is flagged
      setReviews(prev => prev.map(review =>
        review._id === reviewId ? { ...review, flagged: true } : review
      ));

    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to flag review'
      });
    }
  };

  const getRatingStars = (rating) => {
    return '★'.repeat(rating) + '☆'.repeat(5 - rating);
  };

  const getRatingLabel = (rating) => {
    const labels = {
      1: 'Poor',
      2: 'Fair',
      3: 'Good',
      4: 'Very Good',
      5: 'Excellent'
    };
    return labels[rating] || 'Unknown';
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <InstituteSidebar />
        <div className="dashboard-content">
          <div className="loading">Loading reviews...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <InstituteSidebar />
      <div className="dashboard-content">
        <div className="page-header">
          <h1>Student Reviews</h1>
          <div className="header-stats">
            <span className="stat-item">
              Total: <strong>{reviews.length}</strong>
            </span>
            <span className="stat-item">
              Average: <strong>
                {reviews.length > 0 
                  ? (reviews.reduce((sum, review) => sum + review.rating, 0) / reviews.length).toFixed(1)
                  : '0'
                }
              </strong>
            </span>
            <span className="stat-item">
              5 Stars: <strong>{reviews.filter(r => r.rating === 5).length}</strong>
            </span>
          </div>
        </div>

        {/* Reviews Summary */}
        <div className="stats-grid">
          {[5, 4, 3, 2, 1].map(rating => {
            const count = reviews.filter(r => r.rating === rating).length;
            const percentage = reviews.length > 0 ? (count / reviews.length) * 100 : 0;
            
            return (
              <div key={rating} className="stat-card">
                <div className="stat-icon">
                  <span className="rating-stars-small">
                    {'★'.repeat(rating)}
                  </span>
                </div>
                <div className="stat-info">
                  <h3>{count}</h3>
                  <p>{getRatingLabel(rating)}</p>
                  <div className="progress-bar">
                    <div 
                      className="progress-fill"
                      style={{ width: `${percentage}%` }}
                    ></div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        {/* Filters */}
        <div className="filters-card">
          <div className="filter-group">
            <select
              value={ratingFilter}
              onChange={(e) => setRatingFilter(e.target.value)}
              className="filter-select"
            >
              <option value="all">All Ratings</option>
              <option value="5">5 Stars</option>
              <option value="4">4 Stars</option>
              <option value="3">3 Stars</option>
              <option value="2">2 Stars</option>
              <option value="1">1 Star</option>
            </select>
          </div>
        </div>

        {/* Reviews List */}
        <div className="reviews-list-dashboard">
          {filteredReviews.length > 0 ? (
            filteredReviews.map(review => (
              <div key={review._id} className="review-card-dashboard">
                <div className="review-header">
                  <div className="reviewer-info">
                    <div className="reviewer-avatar">
                      {review.user?.name?.charAt(0) || 'A'}
                    </div>
                    <div>
                      <h4>{review.user?.name || 'Anonymous User'}</h4>
                      <span className="review-date">
                        {new Date(review.createdAt).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                  <div className="review-rating">
                    <span className="rating-stars">
                      {getRatingStars(review.rating)}
                    </span>
                    <span className="rating-label">
                      {getRatingLabel(review.rating)}
                    </span>
                  </div>
                </div>

                <div className="review-content">
                  <p>{review.reviewText}</p>
                </div>

                <div className="review-actions">
                  {!review.flagged && (
                    <button
                      onClick={() => handleFlagReview(review._id)}
                      className="btn btn-outline btn-sm"
                    >
                      <i className="fas fa-flag"></i>
                      Flag as Inappropriate
                    </button>
                  )}
                  
                  {review.flagged && (
                    <span className="flagged-badge">
                      <i className="fas fa-flag"></i>
                      Flagged for Review
                    </span>
                  )}
                </div>
              </div>
            ))
          ) : (
            <div className="no-data-card">
              <i className="fas fa-star"></i>
              <h3>No Reviews Found</h3>
              <p>
                {ratingFilter !== 'all' 
                  ? `No ${ratingFilter} star reviews found.`
                  : 'You haven\'t received any reviews yet.'
                }
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Reviews;
