import React, { useState, useEffect } from 'react';
import { adminAPI } from '../../services/api';

const ManageInstitutes = () => {
  const [institutes, setInstitutes] = useState([]);
  const [filter, setFilter] = useState('all'); // all, pending, verified
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchInstitutes();
  }, [filter]);

  const fetchInstitutes = async () => {
    try {
      let response;
      if (filter === 'pending') {
        response = await adminAPI.getPendingInstitutes();
        setInstitutes(response.data);
      } else {
        // For all institutes, you might need to create a separate endpoint
        response = await adminAPI.getPendingInstitutes(); // This should be updated
        setInstitutes(response.data);
      }
    } catch (error) {
      console.error('Error fetching institutes:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleVerification = async (instituteId, verified) => {
    try {
      await adminAPI.verifyInstitute(instituteId, verified);
      fetchInstitutes();
    } catch (error) {
      console.error('Error updating institute:', error);
    }
  };

  const handleStatusChange = async (instituteId, isActive) => {
    try {
      // This endpoint needs to be created
      await adminAPI.updateInstituteStatus(instituteId, isActive);
      fetchInstitutes();
    } catch (error) {
      console.error('Error updating institute status:', error);
    }
  };

  if (loading) {
    return <div className="loading">Loading institutes...</div>;
  }

  return (
    <div className="manage-institutes">
      <div className="section-header">
        <h2>Manage Institutes</h2>
        <div className="filter-tabs">
          <button 
            className={`tab ${filter === 'all' ? 'active' : ''}`}
            onClick={() => setFilter('all')}
          >
            All Institutes
          </button>
          <button 
            className={`tab ${filter === 'pending' ? 'active' : ''}`}
            onClick={() => setFilter('pending')}
          >
            Pending Approval
          </button>
          <button 
            className={`tab ${filter === 'verified' ? 'active' : ''}`}
            onClick={() => setFilter('verified')}
          >
            Verified
          </button>
        </div>
      </div>

      <div className="institutes-list">
        {institutes.map(institute => (
          <div key={institute._id} className="institute-card">
            <div className="institute-info">
              <h3>{institute.name}</h3>
              <p><strong>Category:</strong> {institute.category}</p>
              <p><strong>Affiliation:</strong> {institute.affiliation}</p>
              <p><strong>Contact:</strong> {institute.contact?.email} | {institute.contact?.phone}</p>
              <p><strong>Address:</strong> {institute.address?.city}, {institute.address?.state}</p>
              <p><strong>Status:</strong> 
                <span className={`status ${institute.verified ? 'verified' : 'pending'}`}>
                  {institute.verified ? 'Verified' : 'Pending'}
                </span>
              </p>
              <p><strong>Registered:</strong> {new Date(institute.createdAt).toLocaleDateString()}</p>
            </div>

            <div className="institute-actions">
              {!institute.verified && (
                <>
                  <button 
                    className="btn btn-success"
                    onClick={() => handleVerification(institute._id, true)}
                  >
                    Approve
                  </button>
                  <button 
                    className="btn btn-danger"
                    onClick={() => handleVerification(institute._id, false)}
                  >
                    Reject
                  </button>
                </>
              )}
              
              {institute.verified && (
                <button 
                  className="btn btn-warning"
                  onClick={() => handleStatusChange(institute._id, !institute.isActive)}
                >
                  {institute.isActive ? 'Deactivate' : 'Activate'}
                </button>
              )}
            </div>
          </div>
        ))}

        {institutes.length === 0 && (
          <div className="empty-state">
            No institutes found
          </div>
        )}
      </div>
    </div>
  );
};

export default ManageInstitutes;
