import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { enquiryAPI, instituteAPI } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const EnquiryForm = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();
  const [institutes, setInstitutes] = useState([]);
  const [formData, setFormData] = useState({
    institute: '',
    name: user?.name || '',
    email: user?.email || '',
    phone: '',
    message: ''
  });
  const [loading, setLoading] = useState(false);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    fetchInstitutes();
    
    // If institute ID is passed in state (from institute detail page)
    if (location.state?.instituteId) {
      setFormData(prev => ({
        ...prev,
        institute: location.state.instituteId
      }));
    }
  }, [location.state]);

  const fetchInstitutes = async () => {
    try {
      const response = await instituteAPI.getAll();
      setInstitutes(response.data);
    } catch (error) {
      console.error('Error fetching institutes:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await enquiryAPI.create(formData);
      setSubmitted(true);
      setTimeout(() => {
        navigate('/user/institutes');
      }, 3000);
    } catch (error) {
      console.error('Error submitting enquiry:', error);
      alert('Error submitting enquiry. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getSelectedInstitute = () => {
    return institutes.find(inst => inst._id === formData.institute);
  };

  if (submitted) {
    const institute = getSelectedInstitute();
    
    return (
      <div className="success-message">
        <div className="success-icon">üìß</div>
        <h2>Enquiry Sent Successfully!</h2>
        <p>Your enquiry has been sent to <strong>{institute?.name || 'the institute'}</strong>.</p>
        <p>They will contact you at <strong>{formData.email}</strong> or <strong>{formData.phone}</strong>.</p>
        <p>Redirecting to institutes page...</p>
      </div>
    );
  }

  return (
    <div className="enquiry-form-page">
      <div className="page-header">
        <h1>Send Enquiry</h1>
        <p>Get more information from educational institutes</p>
      </div>

      <div className="form-container">
        <form onSubmit={handleSubmit} className="enquiry-form">
          <div className="form-group">
            <label className="form-label">Select Institute *</label>
            <select
              name="institute"
              value={formData.institute}
              onChange={handleChange}
              className="form-control"
              required
            >
              <option value="">Choose an institute to contact...</option>
              {institutes.map(institute => (
                <option key={institute._id} value={institute._id}>
                  {institute.name} - {institute.city}
                </option>
              ))}
            </select>
          </div>

          {formData.institute && (
            <div className="institute-preview">
              <h4>Contacting:</h4>
              <div className="preview-card">
                <h5>{getSelectedInstitute()?.name}</h5>
                <p>üìç {getSelectedInstitute()?.address}, {getSelectedInstitute()?.city}</p>
                <p>üìû {getSelectedInstitute()?.phone}</p>
                <p>üìß {getSelectedInstitute()?.email}</p>
              </div>
            </div>
          )}

          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">Full Name *</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Email Address *</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label className="form-label">Phone Number *</label>
            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              className="form-control"
              placeholder="+91 9876543210"
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Your Message *</label>
            <textarea
              name="message"
              value={formData.message}
              onChange={handleChange}
              rows="6"
              className="form-control"
              placeholder={`Please include information about:
‚Ä¢ Your educational background
‚Ä¢ Course/program you're interested in
‚Ä¢ Specific questions you have
‚Ä¢ Preferred mode of communication
‚Ä¢ Any other requirements`}
              required
            />
            <div className="form-text">
              Be specific about your requirements to get the most relevant response.
            </div>
          </div>

          <div className="form-actions">
            <button type="submit" disabled={loading} className="btn-primary btn-lg">
              {loading ? 'Sending Enquiry...' : 'Send Enquiry'}
            </button>
            <button 
              type="button" 
              onClick={() => navigate('/user/institutes')}
