import React, { useState } from 'react';
import api from '../../services/api';
import toast from 'react-hot-toast';

const FacilitiesManagement = ({ institute, onUpdate }) => {
  const [facilities, setFacilities] = useState(institute.facilities || []);
  const [newFacility, setNewFacility] = useState('');
  const [loading, setLoading] = useState(false);

  const commonFacilities = [
    'Library', 'Computer Lab', 'Science Lab', 'Sports Ground', 'Cafeteria',
    'Hostel', 'Transport', 'WiFi', 'AC Classrooms', 'Auditorium',
    'Medical Room', 'Playground', 'Swimming Pool', 'Gym', 'Art Room'
  ];

  const handleAddFacility = async () => {
    if (!newFacility.trim()) return;

    const updatedFacilities = [...facilities, newFacility.trim()];
    
    try {
      setLoading(true);
      await api.put('/users/institute-profile', { facilities: updatedFacilities });
      setFacilities(updatedFacilities);
      setNewFacility('');
      toast.success('Facility added successfully!');
      onUpdate();
    } catch (error) {
      toast.error('Failed to add facility');
      console.error('Error adding facility:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRemoveFacility = async (facilityToRemove) => {
    const updatedFacilities = facilities.filter(f => f !== facilityToRemove);
    
    try {
      setLoading(true);
      await api.put('/users/institute-profile', { facilities: updatedFacilities });
      setFacilities(updatedFacilities);
      toast.success('Facility removed successfully!');
      onUpdate();
    } catch (error) {
      toast.error('Failed to remove facility');
      console.error('Error removing facility:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddCommonFacility = (facility) => {
    if (!facilities.includes(facility)) {
      setNewFacility(facility);
    }
  };

  return (
    <div className="facilities-management">
      <div className="section-header">
        <h2>Facilities Management</h2>
        <p>Manage the facilities available at your institute</p>
      </div>

      <div className="facilities-content">
        {/* Add Facility Form */}
        <div className="add-facility-card">
          <h3>Add New Facility</h3>
          <div className="add-facility-form">
            <input
              type="text"
              value={newFacility}
              onChange={(e) => setNewFacility(e.target.value)}
              placeholder="Enter facility name..."
              className="form-control"
            />
            <button 
              onClick={handleAddFacility}
              disabled={loading || !newFacility.trim()}
              className="btn btn-primary"
            >
              {loading ? 'Adding...' : 'Add Facility'}
            </button>
          </div>
        </div>

        {/* Common Facilities */}
        <div className="common-facilities-card">
          <h3>Common Facilities</h3>
          <p>Quickly add commonly used facilities</p>
          <div className="common-facilities-grid">
            {commonFacilities.map((facility) => (
              <button
                key={facility}
                onClick={() => handleAddCommonFacility(facility)}
                disabled={facilities.includes(facility)}
                className={`common-facility-btn ${facilities.includes(facility) ? 'added' : ''}`}
              >
                {facility}
                {facilities.includes(facility) && ' ✓'}
              </button>
            ))}
          </div>
        </div>

        {/* Current Facilities */}
        <div className="current-facilities-card">
          <h3>Current Facilities ({facilities.length})</h3>
          {facilities.length === 0 ? (
            <p className="no-data">No facilities added yet</p>
          ) : (
            <div className="facilities-grid">
              {facilities.map((facility, index) => (
                <div key={index} className="facility-item">
                  <span className="facility-name">{facility}</span>
                  <button
                    onClick={() => handleRemoveFacility(facility)}
                    disabled={loading}
                    className="btn btn-sm btn-danger"
                  >
                    Remove
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default FacilitiesManagement;
