import React, { useState, useEffect } from 'react';
import { userAPI } from '../../services/api';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    fetchUsers();
  }, [filter]);

  const fetchUsers = async () => {
    try {
      const response = await userAPI.getAll();
      let filteredUsers = response.data;
      
      if (filter !== 'all') {
        filteredUsers = response.data.filter(user => user.role === filter);
      }
      
      setUsers(filteredUsers);
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleToggleStatus = async (userId) => {
    try {
      await userAPI.toggleStatus(userId);
      fetchUsers();
      alert('User status updated successfully');
    } catch (error) {
      alert('Error updating user status: ' + error.message);
    }
  };

  const getRoleBadge = (role) => {
    const roleConfig = {
      user: { class: 'user', label: 'Student/Parent' },
      institute: { class: 'institute', label: 'Institute' },
      admin: { class: 'admin', label: 'Admin' }
    };
    
    const config = roleConfig[role] || roleConfig.user;
    return <span className={`role-badge ${config.class}`}>{config.label}</span>;
  };

  const getStatusBadge = (isActive) => {
    return isActive ? (
      <span className="status-badge active">Active</span>
    ) : (
      <span className="status-badge inactive">Inactive</span>
    );
  };

  if (loading) {
    return <div className="loading">Loading users...</div>;
  }

  return (
    <div className="manage-users">
      <div className="section-header">
        <h2>Manage Users</h2>
        <div className="filter-controls">
          <select 
            value={filter} 
            onChange={(e) => setFilter(e.target.value)}
            className="filter-select"
          >
            <option value="all">All Users</option>
            <option value="user">Students/Parents</option>
            <option value="institute">Institutes</option>
            <option value="admin">Admins</option>
          </select>
        </div>
      </div>

      <div className="users-list">
        {users.length === 0 ? (
          <div className="empty-state">
            <h3>No Users Found</h3>
            <p>No users match the current filter criteria.</p>
          </div>
        ) : (
          <div className="users-table">
            <div className="table-header">
              <div className="table-row">
                <div className="table-cell">User</div>
                <div className="table-cell">Role</div>
                <div className="table-cell">Status</div>
                <div className="table-cell">Joined</div>
                <div className="table-cell">Actions</div>
              </div>
            </div>
            
            <div className="table-body">
              {users.map(user => (
                <div key={user._id} className="table-row">
                  <div className="table-cell user-info">
                    <div className="user-avatar">
                      {user.name.charAt(0).toUpperCase()}
                    </div>
                    <div className="user-details">
                      <h4>{user.name}</h4>
                      <p>{user.email}</p>
                      <p>{user.phone}</p>
                    </div>
                  </div>
                  
                  <div className="table-cell">
                    {getRoleBadge(user.role)}
                  </div>
                  
                  <div className="table-cell">
                    {getStatusBadge(user.isActive)}
                  </div>
                  
                  <div className="table-cell">
                    {new Date(user.createdAt).toLocaleDateString()}
                  </div>
                  
                  <div className="table-cell">
                    <button
                      className={`btn-status ${user.isActive ? 'btn-deactivate' : 'btn-activate'}`}
                      onClick={() => handleToggleStatus(user._id)}
                    >
                      {user.isActive ? 'Deactivate' : 'Activate'}
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ManageUsers;
