import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './AdminPanel.css';

const AdminSidebar = ({ isOpen, onClose, onLogout }) => {
  const location = useLocation();

  const menuItems = [
    { path: '/admin/dashboard/analytics', icon: 'ðŸ“Š', label: 'Analytics' },
    { path: '/admin/dashboard/institutes', icon: 'ðŸ«', label: 'Manage Institutes' },
    { path: '/admin/dashboard/users', icon: 'ðŸ‘¥', label: 'Manage Users' },
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <>
      {/* Overlay for mobile */}
      {isOpen && <div className="sidebar-overlay" onClick={onClose}></div>}
      
      <div className={`admin-sidebar ${isOpen ? 'sidebar-open' : ''}`}>
        <div className="sidebar-header">
          <h3>Admin Panel</h3>
          <button className="close-sidebar" onClick={onClose}>Ã—</button>
        </div>
        
        <nav className="sidebar-nav">
          {menuItems.map(item => (
            <Link
              key={item.path}
              to={item.path}
              className={`nav-item ${isActive(item.path) ? 'active' : ''}`}
              onClick={onClose}
            >
              <span className="nav-icon">{item.icon}</span>
              <span className="nav-label">{item.label}</span>
            </Link>
          ))}
        </nav>
        
        <div className="sidebar-footer">
          <button onClick={onLogout} className="logout-btn">
            <span className="nav-icon">ðŸšª</span>
            <span className="nav-label">Logout</span>
          </button>
        </div>
      </div>
    </>
  );
};

export default AdminSidebar;
