import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000, // 10 seconds timeout
});

// Add token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Handle responses and errors
api.interceptors.response.use(
  (response) => {
    console.log('✅ API Response:', response.config.url, response.data);
    return response;
  },
  (error) => {
    console.error('❌ API Error:', {
      url: error.config?.url,
      status: error.response?.status,
      data: error.response?.data,
      message: error.message
    });
    
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    
    // Return a consistent error format
    return Promise.reject({
      message: error.response?.data?.message || error.message || 'Network error',
      status: error.response?.status,
      data: error.response?.data
    });
  }
);

// Auth API
export const authAPI = {
  login: (email, password, role) => 
    api.post('/auth/login', { email, password, role }),
  
  register: (userData) => 
    api.post('/auth/register', userData),
  
  getProfile: () => 
    api.get('/auth/profile')
};

// Institute API
export const instituteAPI = {
  getAll: (params) => api.get('/institutes', { params }),
  getById: (id) => api.get(`/institutes/${id}`),
  getPending: () => api.get('/institutes/pending'),
  update: (id, data) => api.put(`/institutes/${id}`, data),
  verify: (id, action) => api.patch(`/institutes/${id}/verify`, { action }),
  toggleStatus: (id) => api.patch(`/institutes/${id}/status`),
  getStats: (id) => api.get(`/institutes/${id}/stats`)
};

// Review API
export const reviewAPI = {
  create: (data) => api.post('/reviews', data),
  getByInstitute: (instituteId) => api.get(`/reviews/institute/${instituteId}`),
  getAll: () => api.get('/reviews'),
  update: (id, data) => api.put(`/reviews/${id}`, data),
  delete: (id) => api.delete(`/reviews/${id}`),
  moderate: (id, action) => api.patch(`/reviews/${id}/moderate`, { action })
};

// Enquiry API
export const enquiryAPI = {
  create: (data) => api.post('/enquiries', data),
  getByInstitute: (instituteId) => api.get(`/enquiries/institute/${instituteId}`),
  getAll: () => api.get('/enquiries'),
  respond: (id, response) => api.patch(`/enquiries/${id}/respond`, { response })
};

// Course API
export const courseAPI = {
  create: (instituteId, data) => api.post(`/courses/${instituteId}`, data),
  getByInstitute: (instituteId) => api.get(`/courses/institute/${instituteId}`),
  update: (id, data) => api.put(`/courses/${id}`, data),
  delete: (id) => api.delete(`/courses/${id}`)
};

// Facility API
export const facilityAPI = {
  getByInstitute: (instituteId) => api.get(`/facilities/institute/${instituteId}`),
  create: (instituteId, data) => api.post(`/facilities/${instituteId}`, data),
  update: (id, data) => api.put(`/facilities/${id}`, data),
  delete: (id) => api.delete(`/facilities/${id}`),
  toggle: (id) => api.patch(`/facilities/${id}/toggle`)
};

// User API
export const userAPI = {
  getAll: () => api.get('/users'),
  toggleStatus: (id) => api.patch(`/users/${id}/status`),
  getProfile: () => api.get('/users/profile'),
  updateProfile: (data) => api.put('/users/profile', data)
};

// Analytics API
export const analyticsAPI = {
  getPlatform: (timeRange = 'monthly') => 
    api.get(`/analytics/platform?range=${timeRange}`),
  getInstitute: (instituteId) => 
    api.get(`/analytics/institute/${instituteId}`)
};

export default api;
