import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Navigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { instituteService, reviewService, enquiryService } from '../../services/api';
import './UserPanel.css';

const InstituteDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  
  const [institute, setInstitute] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [activeTab, setActiveTab] = useState('overview');
  const [enquiryMessage, setEnquiryMessage] = useState('');
  const [reviewData, setReviewData] = useState({
    rating: 5,
    reviewText: ''
  });
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchInstituteDetail();
  }, [id]);

  const fetchInstituteDetail = async () => {
    try {
      const [instituteRes, reviewsRes] = await Promise.all([
        instituteService.getById(id),
        reviewService.getByInstitute(id)
      ]);
      
      setInstitute(instituteRes.data);
      setReviews(reviewsRes.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching institute details:', error);
      setLoading(false);
    }
  };

  const handleSubmitEnquiry = async (e) => {
    e.preventDefault();
    
    if (!user) {
      navigate('/login');
      return;
    }

    if (!enquiryMessage.trim()) {
      setMessage('Please enter your enquiry message');
      return;
    }

    setSubmitting(true);
    setMessage('');

    try {
      await enquiryService.create({
        institute: id,
        message: enquiryMessage
      });
      
      setMessage('Enquiry submitted successfully!');
      setEnquiryMessage('');
    } catch (error) {
      setMessage('Error submitting enquiry. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    
    if (!user) {
      navigate('/login');
      return;
    }

    if (!reviewData.reviewText.trim()) {
      setMessage('Please enter your review');
      return;
    }

    setSubmitting(true);
    setMessage('');

    try {
      await reviewService.create({
        institute: id,
        rating: reviewData.rating,
        reviewText: reviewData.reviewText
      });
      
      setMessage('Review submitted successfully!');
      setReviewData({ rating: 5, reviewText: '' });
      fetchInstituteDetail(); // Refresh reviews
    } catch (error) {
      setMessage(error.response?.data?.message || 'Error submitting review. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  const getRatingStars = (rating) => {
    return '‚≠ê'.repeat(rating) + '‚òÜ'.repeat(5 - rating);
  };

  if (loading) {
    return <div className="loading">Loading institute details...</div>;
  }

  if (!institute) {
    return <div className="no-data">Institute not found</div>;
  }

  return (
    <div className="institute-detail-page">
      {/* Hero Section */}
      <div className="institute-hero">
        <div className="hero-content">
          <div className="institute-header">
            {institute.logo && (
              <img 
                src={`http://localhost:5000/${institute.logo}`} 
                alt={institute.name}
                className="institute-logo"
              />
            )}
            <div className="institute-basic">
              <h1>{institute.name}</h1>
              <div className="institute-meta">
                <span className="category">{institute.category}</span>
                <span className="affiliation">{institute.affiliation}</span>
                <div className="rating-badge">
                  ‚≠ê {institute.rating?.average || 0}/5 ({institute.rating?.count || 0} reviews)
                </div>
              </div>
              <p className="institute-location">
                üìç {institute.address?.street && `${institute.address.street}, `}
                {institute.address?.city}, {institute.address?.state}
                {institute.address?.pincode && ` - ${institute.address.pincode}`}
              </p>
            </div>
          </div>

          <p className="institute-description">{institute.description}</p>

          <div className="contact-info">
            {institute.contact?.email && (
              <span>üìß {institute.contact.email}</span>
            )}
            {institute.contact?.phone && (
              <span>üìû {institute.contact.phone}</span>
            )}
            {institute.contact?.website && (
              <span>üåê {institute.contact.website}</span>
            )}
          </div>

          {institute.facilities && institute.facilities.length > 0 && (
            <div className="facilities-section">
              <h3>Facilities</h3>
              <div className="facilities-grid">
                {institute.facilities.map((facility, index) => (
                  <div key={index} className="facility-item">
                    <span>‚úì</span>
                    <span>{facility}</span>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Tabs Section */}
      <div className="institute-tabs">
        <div className="tabs-header">
          <button 
            className={`tab ${activeTab === 'overview' ? 'active' : ''}`}
            onClick={() => setActiveTab('overview')}
          >
            Overview
          </button>
          <button 
            className={`tab ${activeTab === 'courses' ? 'active' : ''}`}
            onClick={() => setActiveTab('courses')}
          >
            Courses ({institute.courses?.length || 0})
          </button>
          <button 
            className={`tab ${activeTab === 'reviews' ? 'active' : ''}`}
            onClick={() => setActiveTab('reviews')}
          >
            Reviews ({reviews.length})
          </button>
          <button 
            className={`tab ${activeTab === 'enquiry' ? 'active' : ''}`}
            onClick={() => setActiveTab('enquiry')}
          >
            Contact
          </button>
        </div>

        <div className="tab-content">
          {activeTab === 'overview' && (
            <div className="overview-content">
              <div className="overview-section">
                <h3>About {institute.name}</h3>
                <p>{institute.description}</p>
              </div>

              {institute.images && institute.images.length > 0 && (
                <div className="gallery-section">
                  <h3>Gallery</h3>
                  <div className="gallery-grid">
                    {institute.images.map((image, index) => (
                      <img 
                        key={index}
                        src={`http://localhost:5000/${image}`}
                        alt={`${institute.name} ${index + 1}`}
                        className="gallery-image"
                      />
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}

          {activeTab === 'courses' && (
            <div className="courses-content">
              <h3>Available Courses</h3>
              {institute.courses && institute.courses.length > 0 ? (
                <div className="courses-grid">
                  {institute.courses.map(course => (
                    <div key={course._id} className="course-card">
                      <h4>{course.title}</h4>
                      <p>{course.description}</p>
                      <div className="course-meta">
                        <span>‚è±Ô∏è {course.duration}</span>
                        <span>üí∞ ‚Çπ{course.fees?.toLocaleString()}</span>
                      </div>
                      {course.images && course.images.length > 0 && (
                        <div className="course-images">
                          <img 
                            src={`http://localhost:5000/${course.images[0]}`}
                            alt={course.title}
                          />
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              ) : (
                <p>No courses available at the moment.</p>
              )}
            </div>
          )}

          {activeTab === 'reviews' && (
            <div className="reviews-content">
              <div className="reviews-header">
                <h3>Student Reviews</h3>
                {user && user.role === 'user' && (
                  <button 
                    className="btn-primary"
                    onClick={() => setActiveTab('write-review')}
                  >
                    Write a Review
                  </button>
                )}
              </div>

              {reviews.length === 0 ? (
                <p>No reviews yet. Be the first to review!</p>
              ) : (
                <div className="reviews-list">
                  {reviews.map(review => (
                    <div key={review._id} className="review-card">
                      <div className="review-header">
                        <div className="reviewer-info">
                          <h4>{review.user?.name}</h4>
                          <div className="review-rating">
                            {getRatingStars(review.rating)}
                            <span>({review.rating}/5)</span>
                          </div>
                        </div>
                        <span className="review-date">
                          {new Date(review.createdAt).toLocaleDateString()}
                        </span>
                      </div>
                      <div className="review-content">
                        <p>{review.reviewText}</p>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {activeTab === 'write-review' && (
            <div className="write-review-content">
              <h3>Write a Review</h3>
              {message && (
                <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
                  {message}
                </div>
              )}
              <form onSubmit={handleSubmitReview} className="review-form">
                <div className="form-group">
                  <label>Rating</label>
                  <select
                    value={reviewData.rating}
                    onChange={(e) => setReviewData(prev => ({
                      ...prev,
                      rating: parseInt(e.target.value)
                    }))}
                  >
                    <option value={5}>5 ‚≠ê - Excellent</option>
                    <option value={4}>4 ‚≠ê - Very Good</option>
                    <option value={3}>3 ‚≠ê - Good</option>
                    <option value={2}>2 ‚≠ê - Fair</option>
                    <option value={1}>1 ‚≠ê - Poor</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>Your Review</label>
                  <textarea
                    value={reviewData.reviewText}
                    onChange={(e) => setReviewData(prev => ({
                      ...prev,
                      reviewText: e.target.value
                    }))}
                    placeholder="Share your experience with this institute..."
                    rows="6"
                    required
                  />
                </div>
                <button 
                  type="submit" 
                  className="btn-primary"
                  disabled={submitting}
                >
                  {submitting ? 'Submitting...' : 'Submit Review'}
                </button>
              </form>
            </div>
          )}

          {activeTab === 'enquiry' && (
            <div className="enquiry-content">
              <h3>Contact Institute</h3>
              {message && (
                <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
                  {message}
                </div>
              )}
              <form onSubmit={handleSubmitEnquiry} className="enquiry-form">
                <div className="form-group">
                  <label>Your Message</label>
                  <textarea
                    value={enquiryMessage}
                    onChange={(e) => setEnquiryMessage(e.target.value)}
                    placeholder="Enter your enquiry message here..."
                    rows="6"
                    required
                  />
                </div>
                <button 
                  type="submit" 
                  className="btn-primary"
                  disabled={submitting}
                >
                  {submitting ? 'Sending...' : 'Send Enquiry'}
                </button>
              </form>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default InstituteDetail;
