import React, { useState } from 'react';
import './InstituteDashboard.css';

const FacilitiesManagement = ({ institute, onUpdate }) => {
  const [facilities, setFacilities] = useState(institute.facilities || []);
  const [newFacility, setNewFacility] = useState('');
  const [editingIndex, setEditingIndex] = useState(null);
  const [editValue, setEditValue] = useState('');

  const handleAddFacility = () => {
    if (newFacility.trim() && !facilities.includes(newFacility.trim())) {
      setFacilities([...facilities, newFacility.trim()]);
      setNewFacility('');
      onUpdate();
    }
  };

  const handleEdit = (index) => {
    setEditingIndex(index);
    setEditValue(facilities[index]);
  };

  const handleUpdate = (index) => {
    if (editValue.trim()) {
      const updatedFacilities = [...facilities];
      updatedFacilities[index] = editValue.trim();
      setFacilities(updatedFacilities);
      setEditingIndex(null);
      setEditValue('');
      onUpdate();
    }
  };

  const handleDelete = (index) => {
    const updatedFacilities = facilities.filter((_, i) => i !== index);
    setFacilities(updatedFacilities);
    onUpdate();
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleAddFacility();
    }
  };

  return (
    <div className="facilities-management">
      <h2>Facilities Management</h2>
      
      <div className="add-facility">
        <div className="input-group">
          <input
            type="text"
            value={newFacility}
            onChange={(e) => setNewFacility(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Add a new facility (e.g., Library, Sports Complex)"
          />
          <button onClick={handleAddFacility} className="btn-primary">
            Add Facility
          </button>
        </div>
      </div>

      <div className="facilities-list">
        <h3>Current Facilities ({facilities.length})</h3>
        
        {facilities.length === 0 ? (
          <div className="empty-state">
            <p>No facilities added yet. Start by adding some facilities above.</p>
          </div>
        ) : (
          <div className="facilities-grid">
            {facilities.map((facility, index) => (
              <div key={index} className="facility-card">
                {editingIndex === index ? (
                  <div className="edit-facility">
                    <input
                      type="text"
                      value={editValue}
                      onChange={(e) => setEditValue(e.target.value)}
                      autoFocus
                    />
                    <div className="edit-actions">
                      <button 
                        onClick={() => handleUpdate(index)}
                        className="btn-save"
                      >
                        Save
                      </button>
                      <button 
                        onClick={() => setEditingIndex(null)}
                        className="btn-cancel"
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                ) : (
                  <>
                    <div className="facility-content">
                      <span className="facility-icon">üè¢</span>
                      <span className="facility-name">{facility}</span>
                    </div>
                    <div className="facility-actions">
                      <button 
                        onClick={() => handleEdit(index)}
                        className="btn-edit"
                      >
                        Edit
                      </button>
                      <button 
                        onClick={() => handleDelete(index)}
                        className="btn-delete"
                      >
                        Delete
                      </button>
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Predefined common facilities for quick add */}
      <div className="common-facilities">
        <h4>Common Facilities (Click to Add)</h4>
        <div className="common-facilities-grid">
          {['Library', 'Sports Ground', 'Computer Lab', 'Science Lab', 'Cafeteria', 'Auditorium', 'Medical Room', 'Transport'].map(facility => (
            <button
              key={facility}
              onClick={() => {
                if (!facilities.includes(facility)) {
                  setFacilities([...facilities, facility]);
                  onUpdate();
                }
              }}
              disabled={facilities.includes(facility)}
              className="common-facility-btn"
            >
              {facility}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default FacilitiesManagement;
