import React, { useState, useEffect } from 'react';
import { instituteAPI } from '../../services/api';

const ProfileManagement = ({ institute }) => {
  const [formData, setFormData] = useState({
    name: '',
    category: '',
    affiliation: '',
    address: '',
    city: '',
    state: '',
    phone: '',
    email: '',
    website: '',
    description: '',
    facilities: [],
    logo: null,
    banner: null,
    images: []
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [imagePreviews, setImagePreviews] = useState({
    logo: '',
    banner: '',
    gallery: []
  });

  useEffect(() => {
    if (institute) {
      setFormData({
        name: institute.name || '',
        category: institute.category || '',
        affiliation: institute.affiliation || '',
        address: institute.address || '',
        city: institute.city || '',
        state: institute.state || '',
        phone: institute.phone || '',
        email: institute.email || '',
        website: institute.website || '',
        description: institute.description || '',
        facilities: institute.facilities || [],
        logo: institute.logo || null,
        banner: institute.banner || null,
        images: institute.images || []
      });

      setImagePreviews({
        logo: institute.logo || '',
        banner: institute.banner || '',
        gallery: institute.images || []
      });
    }
  }, [institute]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFacilityChange = (e) => {
    const facilities = e.target.value.split(',').map(f => f.trim()).filter(f => f);
    setFormData(prev => ({
      ...prev,
      facilities
    }));
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    const file = files[0];
    
    if (file) {
      // Check file type
      if (!file.type.startsWith('image/')) {
        setMessage('Please select an image file');
        return;
      }

      // Check file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setMessage('Image size should be less than 5MB');
        return;
      }

      setFormData(prev => ({
        ...prev,
        [name]: file
      }));

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        if (name === 'logo' || name === 'banner') {
          setImagePreviews(prev => ({
            ...prev,
            [name]: e.target.result
          }));
        } else if (name === 'gallery') {
          setImagePreviews(prev => ({
            ...prev,
            gallery: [...prev.gallery, e.target.result]
          }));
          setFormData(prev => ({
            ...prev,
            images: [...prev.images, file]
          }));
        }
      };
      reader.readAsDataURL(file);
    }
  };

  const removeGalleryImage = (index) => {
    setImagePreviews(prev => ({
      ...prev,
      gallery: prev.gallery.filter((_, i) => i !== index)
    }));
    setFormData(prev => ({
      ...prev,
      images: prev.images.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      // Create FormData for file upload
      const submitData = new FormData();
      
      // Append text fields
      Object.keys(formData).forEach(key => {
        if (key !== 'logo' && key !== 'banner' && key !== 'images') {
          if (Array.isArray(formData[key])) {
            formData[key].forEach(item => {
              submitData.append(key, item);
            });
          } else {
            submitData.append(key, formData[key]);
          }
        }
      });

      // Append files
      if (formData.logo && typeof formData.logo !== 'string') {
        submitData.append('logo', formData.logo);
      }
      if (formData.banner && typeof formData.banner !== 'string') {
        submitData.append('banner', formData.banner);
      }
      if (formData.images && formData.images.length > 0) {
        formData.images.forEach((image, index) => {
          if (typeof image !== 'string') {
            submitData.append('images', image);
          }
        });
      }

      await instituteAPI.updateProfile(institute._id, submitData);
      setMessage('Profile updated successfully!');
      
      // Clear file inputs after successful upload
      if (formData.logo && typeof formData.logo !== 'string') {
        document.getElementById('logo').value = '';
      }
      if (formData.banner && typeof formData.banner !== 'string') {
        document.getElementById('banner').value = '';
      }
      if (formData.images && formData.images.some(img => typeof img !== 'string')) {
        document.getElementById('gallery').value = '';
      }
      
    } catch (error) {
      console.error('Error updating profile:', error);
      setMessage('Error updating profile. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="profile-management">
      <div className="page-header">
        <h1>Institute Profile Management</h1>
        <p>Update your institute information and upload images</p>
      </div>

      <form onSubmit={handleSubmit} className="profile-form">
        {/* Image Upload Section */}
        <div className="form-section">
          <h3>Institute Images</h3>
          <div className="image-upload-grid">
            <div className="image-upload-group">
              <label>Institute Logo</label>
              <div className="image-upload-area">
                {imagePreviews.logo ? (
                  <div className="image-preview">
                    <img src={imagePreviews.logo} alt="Institute Logo" />
                    <button 
                      type="button" 
                      className="remove-image"
                      onClick={() => {
                        setFormData(prev => ({ ...prev, logo: null }));
                        setImagePreviews(prev => ({ ...prev, logo: '' }));
                      }}
                    >
                      √ó
                    </button>
                  </div>
                ) : (
                  <div className="upload-placeholder">
                    <span>üì∑</span>
                    <p>Upload Logo</p>
                    <small>Recommended: 200x200px</small>
                  </div>
                )}
                <input
                  type="file"
                  id="logo"
                  name="logo"
                  accept="image/*"
                  onChange={handleFileChange}
                  className="file-input"
                />
              </div>
            </div>

            <div className="image-upload-group">
              <label>Banner Image</label>
              <div className="image-upload-area banner-upload">
                {imagePreviews.banner ? (
                  <div className="image-preview">
                    <img src={imagePreviews.banner} alt="Institute Banner" />
                    <button 
                      type="button" 
                      className="remove-image"
                      onClick={() => {
                        setFormData(prev => ({ ...prev, banner: null }));
                        setImagePreviews(prev => ({ ...prev, banner: '' }));
                      }}
                    >
                      √ó
                    </button>
                  </div>
                ) : (
                  <div className="upload-placeholder">
                    <span>üèûÔ∏è</span>
                    <p>Upload Banner</p>
                    <small>Recommended: 1200x400px</small>
                  </div>
                )}
                <input
                  type="file"
                  id="banner"
                  name="banner"
                  accept="image/*"
                  onChange={handleFileChange}
                  className="file-input"
                />
              </div>
            </div>
          </div>

          <div className="image-upload-group">
            <label>Gallery Images</label>
            <div className="gallery-upload">
              <div className="gallery-preview">
                {imagePreviews.gallery.map((preview, index) => (
                  <div key={index} className="gallery-item">
                    <img src={preview} alt={`Gallery ${index + 1}`} />
                    <button 
                      type="button" 
                      className="remove-image"
                      onClick={() => removeGalleryImage(index)}
                    >
                      √ó
                    </button>
                  </div>
                ))}
                <div className="gallery-upload-area">
                  <div className="upload-placeholder">
                    <span>‚ûï</span>
                    <p>Add Image</p>
                  </div>
                  <input
                    type="file"
                    id="gallery"
                    name="gallery"
                    accept="image/*"
                    onChange={handleFileChange}
                    className="file-input"
                    multiple
                  />
                </div>
              </div>
              <small>Upload multiple images to showcase your campus (max 6 images)</small>
            </div>
          </div>
        </div>

        {/* Basic Information Section */}
        <div className="form-section">
          <h3>Basic Information</h3>
          <div className="form-grid">
            <div className="form-group">
              <label>Institute Name *</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Category *</label>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                required
              >
                <option value="">Select Category</option>
                <option value="School">School</option>
                <option value="College">College</option>
                <option value="University">University</option>
                <option value="Coaching Center">Coaching Center</option>
                <option value="Preschool">Preschool</option>
              </select>
            </div>

            <div className="form-group">
              <label>Affiliation *</label>
              <input
                type="text"
                name="affiliation"
                value={formData.affiliation}
                onChange={handleChange}
                required
              />
            </div>
          </div>
        </div>

        {/* Contact Information Section */}
        <div className="form-section">
          <h3>Contact Information</h3>
          <div className="form-grid">
            <div className="form-group">
              <label>Address *</label>
              <input
                type="text"
                name="address"
                value={formData.address}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>City *</label>
              <input
                type="text"
                name="city"
                value={formData.city}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>State *</label>
              <input
                type="text"
                name="state"
                value={formData.state}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Phone *</label>
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Email *</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Website</label>
              <input
                type="url"
                name="website"
                value={formData.website}
                onChange={handleChange}
              />
            </div>
          </div>
        </div>

        {/* Additional Information Section */}
        <div className="form-section">
          <h3>Additional Information</h3>
          <div className="form-group">
            <label>Description *</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="6"
              required
            />
          </div>

          <div className="form-group">
            <label>Facilities (comma separated)</label>
            <input
              type="text"
              value={formData.facilities.join(', ')}
              onChange={handleFacilityChange}
              placeholder="Library, Sports, Lab, Hostel, etc."
            />
            <small>Separate multiple facilities with commas</small>
          </div>
        </div>

        {message && (
          <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
            {message}
          </div>
        )}

        <button type="submit" disabled={loading} className="btn-primary">
          {loading ? 'Updating...' : 'Update Profile'}
        </button>
      </form>
    </div>
  );
};

export default ProfileManagement;
