import React, { useState, useEffect } from 'react';
import { instituteAPI } from '../../services/api';

const ManageInstitutes = () => {
  const [institutes, setInstitutes] = useState([]);
  const [pendingInstitutes, setPendingInstitutes] = useState([]);
  const [activeTab, setActiveTab] = useState('pending');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchInstitutes();
  }, [activeTab]);

  const fetchInstitutes = async () => {
    try {
      setLoading(true);
      if (activeTab === 'pending') {
        const response = await instituteAPI.getPending();
        setPendingInstitutes(response.data);
      } else {
        const response = await instituteAPI.getAll();
        setInstitutes(response.data.institutes);
      }
    } catch (error) {
      console.error('Error fetching institutes:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleVerification = async (instituteId, action) => {
    try {
      await instituteAPI.verify(instituteId, action);
      fetchInstitutes();
      alert(`Institute ${action === 'approve' ? 'approved' : 'rejected'} successfully`);
    } catch (error) {
      alert('Error processing request: ' + error.message);
    }
  };

  const handleToggleStatus = async (instituteId) => {
    try {
      await instituteAPI.toggleStatus(instituteId);
      fetchInstitutes();
      alert('Institute status updated successfully');
    } catch (error) {
      alert('Error updating status: ' + error.message);
    }
  };

  if (loading) {
    return <div className="loading">Loading institutes...</div>;
  }

  return (
    <div className="manage-institutes">
      <div className="section-header">
        <h2>Manage Institutes</h2>
        <div className="tab-navigation">
          <button
            className={`tab-button ${activeTab === 'pending' ? 'active' : ''}`}
            onClick={() => setActiveTab('pending')}
          >
            Pending Approval ({pendingInstitutes.length})
          </button>
          <button
            className={`tab-button ${activeTab === 'all' ? 'active' : ''}`}
            onClick={() => setActiveTab('all')}
          >
            All Institutes ({institutes.length})
          </button>
        </div>
      </div>

      {activeTab === 'pending' && (
        <div className="institutes-list">
          {pendingInstitutes.length === 0 ? (
            <div className="empty-state">No pending institutes for approval</div>
          ) : (
            pendingInstitutes.map(institute => (
              <div key={institute._id} className="institute-card pending">
                <div className="institute-info">
                  <h3>{institute.name}</h3>
                  <p><strong>Category:</strong> {institute.category}</p>
                  <p><strong>Affiliation:</strong> {institute.affiliation}</p>
                  <p><strong>Location:</strong> {institute.address.city}, {institute.address.state}</p>
                  <p><strong>Contact:</strong> {institute.contact.email} | {institute.contact.phone}</p>
                  <p><strong>Description:</strong> {institute.description}</p>
                </div>
                <div className="action-buttons">
                  <button
                    className="btn-approve"
                    onClick={() => handleVerification(institute._id, 'approve')}
                  >
                    Approve
                  </button>
                  <button
                    className="btn-reject"
                    onClick={() => handleVerification(institute._id, 'reject')}
                  >
                    Reject
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      )}

      {activeTab === 'all' && (
        <div className="institutes-list">
          {institutes.length === 0 ? (
            <div className="empty-state">No institutes found</div>
          ) : (
            institutes.map(institute => (
              <div key={institute._id} className="institute-card">
                <div className="institute-info">
                  <h3>{institute.name}</h3>
                  <p><strong>Category:</strong> {institute.category}</p>
                  <p><strong>Status:</strong> 
                    <span className={`status ${institute.isActive ? 'active' : 'inactive'}`}>
                      {institute.isActive ? 'Active' : 'Inactive'}
                    </span>
                  </p>
                  <p><strong>Verified:</strong> 
                    <span className={`status ${institute.isVerified ? 'verified' : 'unverified'}`}>
                      {institute.isVerified ? 'Yes' : 'No'}
                    </span>
                  </p>
                  <p><strong>Rating:</strong> {institute.avgRating || 'N/A'}</p>
                </div>
                <div className="action-buttons">
                  <button
                    className={`btn-status ${institute.isActive ? 'btn-deactivate' : 'btn-activate'}`}
                    onClick={() => handleToggleStatus(institute._id)}
                  >
                    {institute.isActive ? 'Deactivate' : 'Activate'}
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default ManageInstitutes;
